import React, { useState, useEffect } from 'react';
import { useLocation } from 'react-router-dom';
import api from '../api/axios';
import '../css/CoachDashboard.css'; 
import PlayerModal from './PlayerModal'; 

const CoachDashboard = ({ initialData }) => {
  const [players, setPlayers] = useState(initialData?.players || []);
  const [selectedPlayer, setSelectedPlayer] = useState(null);
  const location = useLocation();

  // 注 专砖转
  useEffect(() => {
    if (!players || players.length === 0) {
      loadPlayers();
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  // --- 转拽: 专注 转 砖专 注专 ---
  useEffect(() => {
    const fetchData = async () => {
        //  专 注 拽砖 专注 (refresh: true)  砖砖 拽砖 驻转 砖拽
        if (location.state?.restorePlayerId) {
            // 拽 注 转 注  注 砖专转
            const updatedPlayers = await loadPlayers();
            
            //  驻转 转 砖拽 拽砖 注 注 砖
            if (updatedPlayers && updatedPlayers.length > 0) {
                const playerToRestore = updatedPlayers.find(p => p._id === location.state.restorePlayerId);
                if (playerToRestore) {
                    setSelectedPlayer(playerToRestore);
                }
            }
        }
    };
    fetchData();
  }, [location.state]);

  const loadPlayers = async () => {
    try {
      const res = await api.get('/players');
      setPlayers(res.data);
      return res.data; // 专 转 转  砖 砖转砖  -useEffect
    } catch (err) {
      console.error("Error loading players:", err);
      return [];
    }
  };

  const handleSave = () => {
    loadPlayers();
  };

  return (
    <div className="coach-container">
      <header className="coach-header">
        <div className="header-title">
            <h1>驻拽  - Mars Power Drive </h1>
        </div>
        <button className="btn-logout" onClick={() => {localStorage.clear(); window.location.href='/login'}}>爪</button>
      </header>

      <div className="players-grid">
        {players.length === 0 ? (
            <p style={{textAlign: 'center', width: '100%', fontSize: '1.2rem', color: '#666'}}>
                注 砖拽  砖 砖拽 注专转...
            </p>
        ) : (
            players.map(p => (
            <div key={p._id} className="player-card" style={{textAlign: 'center'}}>
                <img 
                    src={p.imageUrl} 
                    alt={p.name} 
                    style={{width: '80px', height: '80px', borderRadius: '50%', objectFit: 'cover', border: '3px solid #3498db', marginBottom: '10px'}}
                    onError={(e) => {e.target.src = 'https://via.placeholder.com/80'}} 
                />
                <h3 className="player-name">{p.name}</h3>
                <span style={{fontSize: '14px', color: '#666', display: 'block', marginBottom: '10px'}}>
                #{p.jerseyNumber} | {p.position}
                </span>
                
                <button 
                    className="tab-btn active" 
                    style={{fontSize: '14px', padding: '5px 15px'}}
                    onClick={() => setSelectedPlayer(p)}
                >
                 转转 / 祝 砖拽
                </button>
            </div>
            ))
        )}
      </div>

      {selectedPlayer && (
        <PlayerModal 
            player={selectedPlayer} 
            onClose={() => setSelectedPlayer(null)} 
            onSave={handleSave}
        />
      )}
    </div>
  );
};

export default CoachDashboard;
