import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import '../css/App.css';

const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');

    // --- 爪  (Bypass) ---
    // 驻砖专 住  砖专转  专转 转 注专转 注转
    if (email === 'demo' && password === 'demo') {
        // 砖专转 拽 
        localStorage.setItem('token', 'demo-token-123');
        // 砖专  驻专 砖转砖   砖砖专 注 转 砖
        localStorage.setItem('user_role', 'coach'); // 驻砖专 砖转 -'player'  专转 住 砖拽
        localStorage.setItem('user_name', '砖转砖 ');
        
        navigate('/dashboard');
        return;
    }

    try {
      const response = await fetch('http://localhost:5000/api/login', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ email, password })
      });

      // 转拽 砖 拽转: 拽 拽专 拽住,  住 驻注
      const text = await response.text();
      let data;
      try {
          data = JSON.parse(text);
      } catch (e) {
          //    JSON, 砖转砖 拽住 注爪 砖
          throw new Error(text || '砖 砖专转');
      }

      if (!response.ok) {
        throw new Error(data.message || '砖 转专转');
      }

      localStorage.setItem('token', data.token);
      navigate('/dashboard');

    } catch (err) {
      console.error("Login error:", err);
      setError(err.message);
    }
  };

  return (
    <div className="login-container">
      <div className="card login-card">
        <h2 style={{textAlign: 'center'}}>住 注专转 </h2>
        
        <div style={{background: '#e3f2fd', padding: '10px', borderRadius: '5px', marginBottom: '15px', fontSize: '0.9rem'}}>
            <strong>驻:</strong> 住 专  砖专转, 拽:
            <br/> 砖转砖: <code>demo</code> | 住住: <code>demo</code>
        </div>

        {error && <div className="error-msg">{error}</div>}
        
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label> / 砖 砖转砖</label>
            <input 
              type="text" 
              value={email} 
              onChange={(e) => setEmail(e.target.value)} 
              placeholder="住 ..."
              required 
            />
          </div>
          
          <div className="form-group">
            <label>住住</label>
            <input 
              type="password" 
              value={password} 
              onChange={(e) => setPassword(e.target.value)} 
              placeholder="住 住住..."
              required 
            />
          </div>

          <button type="submit" className="btn-primary full-width">转专</button>
        </form>
      </div>
    </div>
  );
};

export default Login;
