import React, { useState } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import api from '../api/axios'; 
import '../css/FullPage.css';

const ShootingPage = () => {
  const navigate = useNavigate();
  const location = useLocation();
  
  const { text: initialText, isReadOnly, playerId, returnPath } = location.state || {};
  const [text, setText] = useState(initialText || '');

  const handleBack = () => {
      if (returnPath && playerId) {
          navigate(returnPath, { state: { restorePlayerId: playerId, refresh: true } });
      } else {
          navigate(-1);
      }
  };

  const handleSave = async () => {
    if (!playerId) {
      alert('שגיאה: לא נמצא מזהה שחקן, לא ניתן לשמור.');
      return;
    }

    // --- התיקון: יצירת כותרת אוטומטית מהשורה הראשונה ---
    const lines = text.split('\n');
    let newTitle = lines[0] || 'אימון ללא כותרת';
    // אם השורה הראשונה ארוכה מדי, נקצר אותה
    if (newTitle.length > 40) {
        newTitle = newTitle.substring(0, 40) + '...';
    }

    try {
      await api.post(`/propose-update/${playerId}`, {
        trainingPlan: { 
            shootingDrills: text,  // התוכן המלא
            shooting: newTitle     // הכותרת שתופיע בחלונית (למשל "60 זריקות")
        }
      });
      
      alert('✅ התוכנית והכותרת נשמרו בהצלחה!');
      handleBack(); 
    } catch (err) {
      console.error(err);
      alert('❌ שגיאה בשמירה, נסה שוב.');
    }
  };

  return (
    <div className="full-page-container">
      <div className="page-header header-shooting">
        <h1 className="page-title">🏀 אימון קליעה - עריכה מלאה</h1>
        <button className="btn-back" onClick={handleBack}>חזור לחלון השחקן ↩</button>
      </div>

      <div className="content-box">
        {isReadOnly ? (
          <div className="text-content">{text}</div>
        ) : (
          <>
            <textarea 
              className="edit-textarea" 
              value={text} 
              onChange={(e) => setText(e.target.value)}
              placeholder="כתוב כאן את תוכנית האימון..."
            />
            <div className="action-buttons">
              <button className="btn-save" onClick={handleSave}>💾 שמור שינויים וחזור</button>
            </div>
          </>
        )}
      </div>
    </div>
  );
};

export default ShootingPage;
