import React, { useState, useEffect } from 'react';
import axios from 'axios';

const CoachDashboard = () => {
  const [players, setPlayers] = useState([]);
  const [proposals, setProposals] = useState([]);
  const [selectedPlayer, setSelectedPlayer] = useState(null);
  const [newPlan, setNewPlan] = useState('');

  const fetchData = async () => {
    const token = localStorage.getItem('token');
    const res = await axios.get('http://localhost:5000/api/dashboard', { headers: { Authorization: token } });
    setPlayers(res.data.players);
    setProposals(res.data.proposals);
  };

  useEffect(() => { fetchData(); }, []);

  const handleCreateProposal = async () => {
    const token = localStorage.getItem('token');
    await axios.post('http://localhost:5000/api/proposals', {
      playerId: selectedPlayer,
      newPlanData: { shooting: newPlan, fitness: "Unchanged", weightTarget: 0 }
    }, { headers: { Authorization: token } });
    alert("Proposal sent (needs 3 approvals)");
    fetchData();
  };

  const handleApprove = async (id) => {
    try {
      const token = localStorage.getItem('token');
      const res = await axios.post(`http://localhost:5000/api/proposals/approve/${id}`, {}, { headers: { Authorization: token } });
      alert(res.data);
      fetchData();
    } catch (e) { alert(e.response.data); }
  };

  return (
    <div>
      <h2>Coach Panel</h2>
      <div style={{display:'flex', gap:20}}>
        <div style={{flex:1}}>
            <h3>Roster</h3>
            {players.map(p => (
                <div key={p._id} onClick={() => setSelectedPlayer(p._id)} style={{cursor:'pointer', padding:5, borderBottom:'1px solid #ddd'}}>
                    {p.userId.fullName} ({p.position}, {p.height}m)
                </div>
            ))}
            {selectedPlayer && (
                <div className="card">
                    <h4>Edit Plan</h4>
                    <input value={newPlan} onChange={e=>setNewPlan(e.target.value)} placeholder="New Shooting Plan" />
                    <button onClick={handleCreateProposal}>Propose Change</button>
                </div>
            )}
        </div>
        <div style={{flex:1}}>
            <h3>Pending Approvals (3/3 Rule)</h3>
            {proposals.map(p => (
                <div key={p._id} className="proposal-card">
                    <b>Player:</b> {p.playerId?.userId?.fullName}<br/>
                    <b>Proposed By:</b> {p.proposedBy?.fullName}<br/>
                    <b>New Plan:</b> {p.newPlanData?.shooting}<br/>
                    <b>Approvals:</b> {p.approvals.length} / 3 <br/>
                    <button onClick={() => handleApprove(p._id)}>Approve</button>
                </div>
            ))}
        </div>
      </div>
    </div>
  );
};
export default CoachDashboard;
