import React, { useEffect, useState } from 'react';
import './css/PlayerModal.css'; 
import './css/CoachDashboard.css'; 
import ShootingModal from './ShootingModal'; // 

const PlayerDashboard = () => {
  const [player, setPlayer] = useState(null);
  const [showDrills, setShowDrills] = useState(false);

  useEffect(() => {
    const storedUser = localStorage.getItem('user');
    if (storedUser) {
      setPlayer(JSON.parse(storedUser));
    } else {
      window.location.href = '/login';
    }
  }, []);

  const calculateAge = (dateString) => {
    if (!dateString || dateString === ' 爪') return '---';
    const parts = dateString.split('/');
    if (parts.length !== 3) return '---';
    const birthDate = new Date(parts[2], parts[1] - 1, parts[0]);
    const today = new Date();
    let age = today.getFullYear() - birthDate.getFullYear();
    const m = today.getMonth() - birthDate.getMonth();
    if (m < 0 || (m === 0 && today.getDate() < birthDate.getDate())) age--;
    return age;
  };

  if (!player) return <div style={{textAlign:'center', marginTop:'50px'}}>注 转...</div>;

  return (
    <div className="coach-container">
      <header className="coach-header">
         <div className="header-title"><h1>专 砖 - Mars Power Drive </h1></div>
         <button className="btn-logout" onClick={() => {localStorage.clear(); window.location.href='/login'}}>爪</button>
      </header>

      <div style={{display:'flex', justifyContent:'center', marginTop:'20px'}}>
        <div className="player-modal-content" style={{width:'100%', maxWidth:'1000px', boxShadow:'none', border:'1px solid #ddd', position: 'static', transform: 'none'}}>
            <div className="modal-body">
                <div className="player-details-side">
                    <img src={player.imageUrl} alt={player.name} className="player-large-img" onError={(e) => {e.target.src = 'https://via.placeholder.com/150'}} />
                    <h2 className="player-name-red">{player.name}</h2>
                    <div className="details-list">
                        <div className="detail-row"><strong> 注:</strong> <span>{player.position}</span></div>
                        <div className="detail-row"><strong> 住驻专 驻:</strong> <span>#{player.jerseyNumber}</span></div>
                        <div className="detail-row"><strong> :</strong> <span>{player.height} '</span></div>
                        <div className="detail-row"><strong> 转专 :</strong> <span>{player.birthDate}</span></div>
                        <div className="detail-row"><strong> :</strong> <span>{calculateAge(player.birthDate)}</span></div>
                    </div>
                </div>

                <div className="form-side">
                    <h2 className="form-title"> 转转 拽爪注转 砖</h2>
                    
                    <div className="form-group">
                        <label className="form-label">  拽注 (抓 爪驻 驻专):</label>
                        <div 
                            onClick={() => setShowDrills(true)}
                            style={{cursor: 'pointer', border: '2px solid #3498db', padding: '12px', borderRadius: '8px', backgroundColor: '#f0f8ff', color: '#3498db', fontWeight: 'bold'}}
                        >
                             {player.trainingPlan?.shooting || ' 转'}  抓  驻专 
                        </div>
                    </div>

                    <div className="form-group">
                        <label className="form-label"> 砖专 驻:</label>
                        <input className="form-input" value={player.trainingPlan?.fitness || ' 转'} readOnly style={{backgroundColor: '#f9f9f9', color: '#555', cursor: 'default'}} />
                    </div>

                    <div className="form-group">
                        <label className="form-label">ウ 转:</label>
                        <input className="form-input" value={player.nutritionPlan || ' 转'} readOnly style={{backgroundColor: '#f9f9f9', color: '#555', cursor: 'default'}} />
                    </div>
                    
                    <div style={{marginTop: '30px', textAlign: 'center', color: '#7f8c8d', borderTop: '1px solid #eee', paddingTop: '10px'}}>
                        <em>* 砖 转转 砖 驻转 爪转 拽爪注</em>
                    </div>
                </div>
            </div>
        </div>
      </div>

      {/*  拽驻抓 拽专  */}
      <ShootingModal 
        isOpen={showDrills}
        onClose={() => setShowDrills(false)}
        drills={player.trainingPlan?.shootingDrills || '  驻专'}
        isReadOnly={true} // 砖拽   注专
      />
    </div>
  );
};

export default PlayerDashboard;
