import React, { useState } from 'react';
import api from '../api/axios';
import { useNavigate } from 'react-router-dom';

const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await api.post('/auth/login', { email, password });
      
      // 砖专转 拽 驻专 砖转砖
      localStorage.setItem('token', res.data.token);
      localStorage.setItem('user', JSON.stringify(res.data.user)); 

      // 拽转 驻 驻 转驻拽
      if (res.data.user.role === 'player') {
        navigate('/player-zone'); // 驻 专 砖 砖 砖拽
      } else {
        navigate('/dashboard');   // 驻  
      }
      
    } catch (err) {
      alert('砖 转专转: 驻专 砖');
    }
  };

  return (
    <div style={{display:'flex', justifyContent:'center', alignItems:'center', height:'100vh', backgroundColor:'#1a1a2e', color:'white'}}>
      <div style={{backgroundColor:'#16213e', padding:'40px', borderRadius:'10px', textAlign:'center', width:'350px'}}>
        <h2 style={{color:'#e94560'}}> MARS ACCESS</h2>
        <form onSubmit={handleSubmit} style={{display:'flex', flexDirection:'column', gap:'15px'}}>
          <input 
            type="email" 
            placeholder="" 
            value={email} onChange={e=>setEmail(e.target.value)}
            style={{padding:'10px', borderRadius:'5px', border:'none', textAlign: 'right'}}
          />
          <input 
            type="password" 
            placeholder="住住" 
            value={password} onChange={e=>setPassword(e.target.value)}
            style={{padding:'10px', borderRadius:'5px', border:'none', textAlign: 'right'}}
          />
          <button type="submit" style={{padding:'10px', backgroundColor:'#e94560', color:'white', border:'none', borderRadius:'5px', cursor:'pointer', fontWeight:'bold'}}>
            转专 注专转
          </button>
        </form>
      </div>
    </div>
  );
};

export default Login;
