import React, { useState, useEffect } from 'react';

const ShootingModal = ({ isOpen, onClose, drills, onSave, isReadOnly }) => {
  const [text, setText] = useState(drills);

  useEffect(() => {
    setText(drills);
  }, [drills]);

  if (!isOpen) return null;

  return (
    <div style={overlayStyle}>
      <div style={modalStyle}>
        <div style={headerStyle}>
            <h2>üèÄ ◊™◊ï◊õ◊†◊ô◊™ ◊¢◊ë◊ï◊ì◊î ◊û◊ß◊¶◊ï◊¢◊ô◊™</h2>
            <button onClick={onClose} style={closeBtnStyle}>‚úñ</button>
        </div>

        <div style={{flex: 1, overflowY: 'auto', marginBottom: '20px'}}>
            {isReadOnly ? (
                // ◊û◊¶◊ë ◊ß◊®◊ô◊ê◊î ◊ú◊©◊ó◊ß◊ü (◊©◊ï◊û◊® ◊¢◊ú ◊¢◊ô◊¶◊ï◊ë ◊î◊©◊ï◊®◊ï◊™)
                <div style={readOnlyStyle}>{text}</div>
            ) : (
                // ◊û◊¶◊ë ◊¢◊®◊ô◊õ◊î ◊ú◊û◊ê◊û◊ü
                <textarea 
                    style={textareaStyle}
                    value={text}
                    onChange={(e) => setText(e.target.value)}
                />
            )}
        </div>

        {!isReadOnly && (
            <div style={{textAlign: 'center'}}>
                <button onClick={() => onSave(text)} style={saveBtnStyle}>üíæ ◊©◊û◊ï◊® ◊©◊ô◊†◊ï◊ô◊ô◊ù ◊ú◊©◊ó◊ß◊ü ◊ñ◊î</button>
            </div>
        )}
      </div>
    </div>
  );
};

// --- ◊¢◊ô◊¶◊ï◊ë◊ô◊ù ---
const overlayStyle = {
  position: 'fixed', top: 0, left: 0, right: 0, bottom: 0,
  backgroundColor: 'rgba(0,0,0,0.85)', zIndex: 2000,
  display: 'flex', justifyContent: 'center', alignItems: 'center'
};

const modalStyle = {
  backgroundColor: '#fff', width: '90%', maxWidth: '600px', height: '80vh',
  borderRadius: '15px', padding: '25px', display: 'flex', flexDirection: 'column',
  boxShadow: '0 10px 30px rgba(0,0,0,0.5)', direction: 'rtl'
};

const headerStyle = {
    display: 'flex', justifyContent: 'space-between', alignItems: 'center',
    borderBottom: '2px solid #eee', paddingBottom: '10px', marginBottom: '15px', color: '#e67e22'
};

const closeBtnStyle = {
    background: 'none', border: 'none', fontSize: '24px', cursor: 'pointer', color: '#7f8c8d'
};

const textareaStyle = {
    width: '100%', height: '100%', padding: '15px', fontSize: '16px', fontFamily: 'inherit',
    border: '2px solid #3498db', borderRadius: '10px', resize: 'none', outline: 'none',
    lineHeight: '1.6', backgroundColor: '#fdfdfd'
};

const readOnlyStyle = {
    whiteSpace: 'pre-wrap', fontSize: '16px', lineHeight: '1.8', color: '#2c3e50',
    backgroundColor: '#f8f9fa', padding: '15px', borderRadius: '10px', border: '1px solid #ddd',
    height: '100%', overflowY: 'auto'
};

const saveBtnStyle = {
    backgroundColor: '#27ae60', color: 'white', padding: '12px 30px', border: 'none',
    borderRadius: '8px', fontSize: '16px', cursor: 'pointer', fontWeight: 'bold'
};

export default ShootingModal;
