import React from 'react';
import { useNavigate } from 'react-router-dom';
import '../css/PlayerModal.css';

const PlayerModal = ({ player, onClose }) => {
  const navigate = useNavigate();
  
  // Helper to calculate age from birthdate string (DD/MM/YYYY)
  const calculateAge = (dateString) => {
    if (!dateString) return '';
    const parts = dateString.split('/');
    if (parts.length < 3) return '';
    const birthDate = new Date(parts[2], parts[1] - 1, parts[0]);
    const ageDifMs = Date.now() - birthDate.getTime();
    return Math.abs(new Date(ageDifMs).getUTCFullYear() - 1970);
  };

  // Navigation handler that preserves current data state
  const navigateToPage = (path, textData, titleData = '') => {
      navigate(path, { 
          state: { 
              text: textData, 
              title: titleData,
              // Pass the full plan to ensure no data is lost during navigation
              fullPlan: player.trainingPlan, 
              playerId: player._id, 
              returnPath: '/dashboard'
          } 
      });
  };

  if (!player) return null;

  return (
    <div className="player-modal-overlay">
      <div className="player-modal-content">
        <button onClick={onClose} className="close-btn">‚úñ ◊°◊í◊ï◊®</button>
        
        <div className="modal-body">
            {/* Right Side - Player Details */}
            <div className="player-details-side">
                <img src={player.imageUrl} alt={player.name} className="player-large-img" onError={(e) => e.target.src = 'https://via.placeholder.com/150'}/>
                <h2 className="player-name-red">{player.name}</h2>
                <div className="details-list">
                    <p><strong>üìç ◊¢◊û◊ì◊î:</strong> {player.position}</p>
                    <p><strong>üëï ◊û◊°◊§◊®:</strong> {player.jerseyNumber}</p>
                    <p><strong>üìè ◊í◊ï◊ë◊î:</strong> {player.height} ◊û'</p>
                    <p><strong>üéÇ ◊í◊ô◊ú:</strong> {calculateAge(player.birthDate)}</p>
                </div>
            </div>

            {/* Left Side - Professional Plans */}
            <div className="form-side">
                <h2 className="form-title">üìã ◊™◊ï◊õ◊†◊ô◊™ ◊¢◊ë◊ï◊ì◊î ◊û◊ß◊¶◊ï◊¢◊ô◊™</h2>
                
                {/* Shooting Plan */}
                <div className="form-group">
                    <label className="form-label">üèÄ ◊ê◊ô◊û◊ï◊ü ◊ß◊ú◊ô◊¢◊î:</label>
                    <div 
                        className="clickable-input orange-border"
                        onClick={() => navigateToPage('/shooting', player?.trainingPlan?.shootingDrills, player?.trainingPlan?.shooting)}
                    >
                        <span>{player?.trainingPlan?.shooting || '◊ê◊ô◊ü ◊™◊ï◊õ◊†◊ô◊™'}</span>
                        <span>‚úé ◊¢◊®◊ï◊ö</span>
                    </div>
                </div>

                {/* Fitness Plan */}
                <div className="form-group">
                    <label className="form-label">üí™ ◊õ◊ï◊©◊® ◊í◊ï◊§◊†◊ô:</label>
                    <div 
                        className="clickable-input green-border"
                        onClick={() => navigateToPage('/fitness', player?.trainingPlan?.fitnessDrills)}
                    >
                        <span>{player?.trainingPlan?.fitness || '◊™◊ï◊õ◊†◊ô◊™ ◊õ◊ï◊©◊®'}</span>
                        <span>üëÅÔ∏è ◊¶◊§◊î</span>
                    </div>
                </div>

                {/* Nutrition Plan */}
                <div className="form-group">
                    <label className="form-label">ü•¶ ◊™◊ñ◊ï◊†◊î:</label>
                    <div 
                        className="clickable-input blue-border"
                        onClick={() => navigateToPage('/nutrition', player?.nutritionMenu)}
                    >
                        <span>{player?.nutritionPlan || '◊™◊§◊®◊ô◊ò ◊™◊ñ◊ï◊†◊î'}</span>
                        <span>ü•ó ◊¶◊§◊î</span>
                    </div>
                </div>

                <div className="btn-actions">
                    <button onClick={onClose} className="btn-cancel" style={{width:'100%'}}>◊°◊í◊ï◊® ◊ó◊ú◊ï◊ü</button>
                </div>
            </div>
        </div>
      </div>
    </div>
  );
};

export default PlayerModal;
