import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import CoachDashboard from './CoachDashboard';
import PlayerDashboard from './PlayerDashboard';
import api from '../api/axios';
import '../css/Dashboard.css';

const Dashboard = () => {
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [userRole, setUserRole] = useState(null);
  const [userData, setUserData] = useState(null);
  const navigate = useNavigate();

  const handleLogout = () => {
    localStorage.clear();
    navigate('/login');
  };

  useEffect(() => {
    const identifyUser = async () => {
      try {
        // פונים לשרת ומקבלים את כל המידע במכה אחת
        const res = await api.get('/dashboard');
        
        // השרת מחזיר את התפקיד בתוך האובייקט
        const role = res.data.user ? res.data.user.role : res.data.role;
        
        setUserRole(role);
        setUserData(res.data);
        setLoading(false);

      } catch (err) {
        console.error("Dashboard Error:", err);
        setError("לא ניתן לטעון נתונים. נסה להתחבר מחדש.");
        // אם הטוקן לא בתוקף, זורקים ללוגין
        if (err.response && err.response.status === 401) {
            setTimeout(handleLogout, 2000);
        }
      }
    };

    identifyUser();
  }, [navigate]);

  if (loading) return <div className="loader-container"><div className="loader"></div><h3>טוען מערכת...</h3></div>;
  if (error) return <div className="dashboard-container center-content"><h2 className="error-text">{error}</h2></div>;

  // מעבירים את המידע (userData) לרכיבים הבאים
  return (userRole === 'head-coach' || userRole === 'coach')
    ? <CoachDashboard initialData={userData} />
    : <PlayerDashboard initialData={userData} />;
};

export default Dashboard;
