import React, { useEffect, useState } from 'react';
import api from '../api/axios';
import { LineChart, Line, XAxis, YAxis, Tooltip, ResponsiveContainer } from 'recharts';

const PlayerDashboard = () => {
  const [data, setData] = useState(null);

  useEffect(() => {
    api.get('/team/my-stats').then(res => setData(res.data)).catch(console.error);
  }, []);

  if (!data) return <div className="loading">注 驻专驻...</div>;

  const mockGraph = [
    { name: 'W1', val: 60 }, { name: 'W2', val: 65 }, { name: 'W3', val: 63 }, { name: 'W4', val: 70 }
  ];

  return (
    <div className="dashboard-container">
      <header className="dash-header">
        <h1>Mars ID: {data.name}</h1>
        <button onClick={() => {localStorage.clear(); window.location.href='/login'}}>爪</button>
      </header>

      <div className="stats-container">
        <div className="stat-box">
           <h3> 转转 </h3>
           <p><strong>拽注:</strong> {data.trainingPlan.shooting}</p>
           <p><strong>砖专:</strong> {data.trainingPlan.fitness}</p>
        </div>
        <div className="stat-box">
           <h3> 转</h3>
           <p>{data.nutritionPlan}</p>
        </div>
      </div>

      <div className="chart-box">
        <h3>转拽转 爪注</h3>
        <div style={{height: 300}}>
          <ResponsiveContainer>
            <LineChart data={mockGraph}>
              <XAxis dataKey="name" stroke="#fff" />
              <YAxis stroke="#fff" />
              <Tooltip />
              <Line type="monotone" dataKey="val" stroke="#e94560" strokeWidth={3} />
            </LineChart>
          </ResponsiveContainer>
        </div>
      </div>
    </div>
  );
};

export default PlayerDashboard;
