import React, { useState, useEffect } from 'react';
import CoachDashboard from './CoachDashboard';
import PlayerDashboard from './PlayerDashboard';
import '../css/Dashboard.css';

const Dashboard = () => {
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [userRole, setUserRole] = useState(null);
  const [userData, setUserData] = useState(null);

  const handleLogout = () => {
    localStorage.clear();
    sessionStorage.clear();
    window.location.href = '/login';
  };

  useEffect(() => {
    const identifyUser = async () => {
      try {
        const token = localStorage.getItem('token');
        if (!token) throw new Error("לא נמצא אישור כניסה. נא להתחבר מחדש.");

        // שליפת נתונים ראשונית כדי להבין מי המשתמש
        const response = await fetch('http://localhost:5000/api/dashboard', {
          headers: { 'Authorization': `Bearer ${token}` }
        });

        if (!response.ok) {
            if (response.status === 401 || response.status === 403) {
                throw new Error("פג תוקף החיבור.");
            }
            throw new Error("שגיאה בטעינת הנתונים.");
        }

        const result = await response.json();
        
        // זיהוי התפקיד לפי המידע שחזר מהשרת
        // הנחה: השרת מחזיר user.role או מבנה שמסגיר את התפקיד
        const role = result.user?.role || 'player'; // ברירת מחדל: שחקן
        
        setUserRole(role);
        setUserData(result); // מעבירים את המידע לקומפוננטות הבנות כדי לחסוך קריאה
        setLoading(false);

      } catch (err) {
        console.error(err);
        setError(err.message);
        setLoading(false);
      }
    };

    identifyUser();
  }, []);

  if (loading) {
    return (
      <div className="dashboard-container center-content">
        <div className="loader"></div>
        <h2>טוען מערכת...</h2>
        <button onClick={handleLogout} className="btn-link">נתקע? לחץ ליציאה</button>
      </div>
    );
  }

  if (error) {
    return (
      <div className="dashboard-container center-content">
        <h2 className="error-text">שגיאה: {error}</h2>
        <button onClick={handleLogout} className="btn-logout">חזור למסך התחברות</button>
      </div>
    );
  }

  // ניתוב לקומפוננטה המתאימה לפי תפקיד
  if (userRole === 'coach') {
    return <CoachDashboard initialData={userData} onLogout={handleLogout} />;
  } else {
    return <PlayerDashboard initialData={userData} onLogout={handleLogout} />;
  }
};

export default Dashboard;
