import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import CoachDashboard from './CoachDashboard';
import PlayerDashboard from './PlayerDashboard';
import '../css/Dashboard.css';

const Dashboard = () => {
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [userRole, setUserRole] = useState(null);
  const [userData, setUserData] = useState(null);
  const navigate = useNavigate();

  const handleLogout = () => {
    localStorage.clear();
    navigate('/login');
  };

  useEffect(() => {
    const identifyUser = async () => {
      try {
        const token = localStorage.getItem('token');
        
        if (!token) {
            // אם אין טוקן, נחזיר את המשתמש ללוגין במקום לקרוס
            navigate('/login');
            return;
        }

        const response = await fetch('http://localhost:5000/api/dashboard', {
          headers: { 'Authorization': `Bearer ${token}` }
        });

        if (!response.ok) {
            if (response.status === 401 || response.status === 403) {
                handleLogout(); // טוקן לא בתוקף
                return;
            }
            throw new Error("שגיאה בטעינת הנתונים מהשרת");
        }

        const result = await response.json();
        const role = result.user?.role || 'player'; 
        
        setUserRole(role);
        setUserData(result);
        setLoading(false);

      } catch (err) {
        console.error(err);
        setError(err.message);
        setLoading(false);
      }
    };

    identifyUser();
  }, [navigate]);

  if (loading) return <div className="loader-container"><div className="loader"></div><h3>טוען נתונים...</h3></div>;

  if (error) {
    return (
      <div className="dashboard-container center-content">
        <h2 className="error-text">שגיאה: {error}</h2>
        <button onClick={handleLogout} className="btn-logout">חזור למסך התחברות</button>
      </div>
    );
  }

  return userRole === 'coach' 
    ? <CoachDashboard initialData={userData} onLogout={handleLogout} />
    : <PlayerDashboard initialData={userData} onLogout={handleLogout} />;
};

export default Dashboard;
