import React, { useState } from 'react';
import api from '../api/axios';
import '../css/PlayerModal.css';
import ShootingModal from './ShootingModal';
import FitnessModal from './FitnessModal'; // 1. ייבוא

const PlayerModal = ({ player, onClose, onSave }) => {
  const [form, setForm] = useState({
    shooting: player.trainingPlan?.shooting || '',
    shootingDrills: player.trainingPlan?.shootingDrills || '',
    fitness: player.trainingPlan?.fitness || '',
    fitnessDrills: player.trainingPlan?.fitnessDrills || '', // שדה חדש מה-DB
    nutrition: player.nutritionPlan || ''
  });
  
  const [showDrills, setShowDrills] = useState(false);
  const [showFitness, setShowFitness] = useState(false); // 2. סטייט לכושר

  const calculateAge = (dateString) => {
    if (!dateString) return '';
    const parts = dateString.split('/');
    const birthDate = new Date(parts[2], parts[1] - 1, parts[0]);
    const ageDifMs = Date.now() - birthDate.getTime();
    const ageDate = new Date(ageDifMs); 
    return Math.abs(ageDate.getUTCFullYear() - 1970);
  };

  const handleSubmit = async () => {
    try {
      await api.post(`/propose-update/${player._id}`, {
        trainingPlan: {
            shooting: form.shooting,
            shootingDrills: form.shootingDrills,
            fitness: form.fitness,
            fitnessDrills: form.fitnessDrills // שומרים גם את הטקסט הארוך
        },
        nutritionPlan: form.nutrition
      });
      alert('התוכנית עודכנה בהצלחה!');
      onSave(); 
      onClose();
    } catch (err) {
      console.error(err);
      alert('שגיאה בעדכון');
    }
  };

  const handleDrillsUpdate = (newDrills, newSummary) => {
      setForm({ ...form, shootingDrills: newDrills, shooting: newSummary });
      setShowDrills(false); 
  };

  return (
    <div className="player-modal-overlay">
      <div className="player-modal-content">
        <button onClick={onClose} className="close-btn">✖ סגור</button>
        
        <div className="modal-body">
            {/* צד ימין - פרטי שחקן */}
            <div className="player-details-side">
                <img src={player.imageUrl} alt={player.name} className="player-large-img" onError={(e) => e.target.src = 'https://via.placeholder.com/150'}/>
                <h2 className="player-name-red">{player.name}</h2>
                <div className="details-list">
                    <p><strong>📍 עמדה:</strong> {player.position}</p>
                    <p><strong>👕 מספר:</strong> {player.jerseyNumber}</p>
                    <p><strong>📏 גובה:</strong> {player.height} מ'</p>
                    <p><strong>🎂 גיל:</strong> {calculateAge(player.birthDate)}</p>
                </div>
            </div>

            {/* צד שמאל - טופס */}
            <div className="form-side">
                <h2 className="form-title">📋 תוכנית עבודה מקצועית</h2>
                
                {/* קליעה */}
                <div className="form-group">
                    <label className="form-label">🏀 אימון קליעה (לחץ לפירוט ועריכה):</label>
                    <div 
                        onClick={() => setShowDrills(true)}
                        className="clickable-input orange-border"
                    >
                        {form.shooting || 'לחץ להוספת אימון'}
                    </div>
                </div>

                {/* כושר גופני - החלק החדש */}
                <div className="form-group">
                    <label className="form-label">💪 כושר גופני (לחץ לצפייה בתוכנית):</label>
                    <div 
                        onClick={() => setShowFitness(true)} // פתיחת המודל
                        style={{
                            cursor: 'pointer',
                            border: '2px solid #27ae60', // מסגרת ירוקה
                            borderRadius: '8px',
                            padding: '10px',
                            backgroundColor: '#f0fff4',
                            color: '#27ae60',
                            fontWeight: 'bold',
                            display: 'flex', justifyContent: 'space-between'
                        }}
                    >
                        <span>{form.fitness || 'לחץ לצפייה בתוכנית'}</span>
                        <span style={{fontSize:'0.8em'}}>📄 לחץ לפירוט</span>
                    </div>
                </div>

                <div className="form-group">
                    <label className="form-label">🥦 תזונה:</label>
                    <input className="form-input" value={form.nutrition} onChange={e=>setForm({...form, nutrition: e.target.value})} />
                </div>

                <div className="btn-actions">
                    <button onClick={handleSubmit} className="btn-save">💾 שמור עדכונים לשרת</button>
                    <button onClick={onClose} className="btn-cancel">ביטול</button>
                </div>
            </div>
        </div>
      </div>

      {/* המודלים */}
      <ShootingModal 
        isOpen={showDrills} 
        onClose={() => setShowDrills(false)} 
        drills={form.shootingDrills} 
        onSave={handleDrillsUpdate} 
        isReadOnly={false} 
      />

      <FitnessModal 
        isOpen={showFitness}
        onClose={() => setShowFitness(false)}
        planText={form.fitnessDrills} // מעבירים את הטקסט מה-DB
      />
    </div>
  );
};

export default PlayerModal;
