import React, { useState, useEffect, useCallback } from 'react';
import { BrowserRouter as Router, Routes, Route, useLocation } from 'react-router-dom';
import Login from './components/Login';
import CoachDashboard from './components/CoachDashboard';
import PlayerDashboard from './components/PlayerDashboard';
import ShootingPage from './components/ShootingPage';
import FitnessPage from './components/FitnessPage';
import NutritionPage from './components/NutritionPage';

// --- ייבוא שומר המסך החדש ---
import Screensaver from './components/Screensaver';

// רכיב עוטף שיטפל בלוגיקה של הטיימר (חייב להיות בתוך Router כדי להשתמש ב-useLocation)
const AppContent = () => {
  const location = useLocation();
  const [isScreensaverActive, setIsScreensaverActive] = useState(false);
  
  // האם אנחנו בדף הלוגין? (אם כן, לא נפעיל שומר מסך)
  const isLoginPage = location.pathname === '/' || location.pathname === '/login';

  // פונקציה שמפעילה את שומר המסך
  const activateScreensaver = useCallback(() => {
    if (!isLoginPage) {
      setIsScreensaverActive(true);
    }
  }, [isLoginPage]);

  // פונקציה שמאפסת את הטיימר (קורית כשזזים/לוחצים)
  const resetTimer = useCallback(() => {
    setIsScreensaverActive(false);
    
    // ניקוי הטיימר הקודם
    if (window.screensaverTimeout) {
      clearTimeout(window.screensaverTimeout);
    }

    // הגדרת טיימר חדש לדקה (60000 מילי-שניות)
    if (!isLoginPage) {
      window.screensaverTimeout = setTimeout(activateScreensaver, 60000); 
    }
  }, [activateScreensaver, isLoginPage]);

  useEffect(() => {
    // 1. הגדרת מאזינים לאירועים (עכבר, מקלדת, לחיצה)
    const events = ['mousemove', 'keydown', 'click', 'scroll', 'touchstart'];
    
    events.forEach(event => {
      window.addEventListener(event, resetTimer);
    });

    // 2. הפעלה ראשונית:
    // אם עברנו לוגין (אנחנו לא בדף לוגין), נפעיל את שומר המסך מיד בהתחלה
    if (!isLoginPage) {
        setIsScreensaverActive(true); // מתחיל פעיל כשנכנסים למערכת
        resetTimer(); // ומתחיל לספור לדקה הבאה
    } else {
        setIsScreensaverActive(false); // בדף לוגין הוא כבוי
    }

    // ניקוי ביציאה
    return () => {
      events.forEach(event => {
        window.removeEventListener(event, resetTimer);
      });
      if (window.screensaverTimeout) clearTimeout(window.screensaverTimeout);
    };
  }, [resetTimer, isLoginPage]);

  return (
    <>
      {/* הצגת שומר המסך מעל הכל */}
      <Screensaver isActive={isScreensaverActive} />

      <Routes>
        <Route path="/" element={<Login />} />
        <Route path="/login" element={<Login />} />
        
        <Route path="/dashboard" element={<CoachDashboard />} />
        <Route path="/player-zone" element={<PlayerDashboard />} />

        <Route path="/shooting" element={<ShootingPage />} />
        <Route path="/fitness" element={<FitnessPage />} />
        <Route path="/nutrition" element={<NutritionPage />} />
      </Routes>
    </>
  );
};

function App() {
  return (
    <Router>
      <AppContent />
    </Router>
  );
}

export default App;
