import React, { useState, useEffect } from 'react';
import axios from 'axios';

const PlayerDashboard = () => {
  const [data, setData] = useState(null);

  useEffect(() => {
    const getData = async () => {
      const token = localStorage.getItem('token');
      const res = await axios.get('http://localhost:5000/api/dashboard', {
        headers: { Authorization: token }
      });
      setData(res.data);
    };
    getData();
  }, []);

  if (!data) return <div>Loading...</div>;

  return (
    <div>
      <h2>My Profile: {data.userId.fullName}</h2>
      <div className="card">
        <h3>Training Plan</h3>
        <p>Shooting: {data.trainingPlan.shooting}</p>
        <p>Fitness: {data.trainingPlan.fitness}</p>
      </div>
      <div className="card">
        <h3>Progress</h3>
        <div style={{display:'flex', alignItems:'flex-end', height:100, gap:5}}>
            {data.stats.map((s,i) => (
                <div key={i} style={{width:20, height:`${s.shootingPercentage}%`, background:'blue'}}></div>
            ))}
        </div>
      </div>
    </div>
  );
};
export default PlayerDashboard;
