import React, { useState, useEffect } from 'react';
import api from '../api/axios';

const CoachDashboard = ({ initialData }) => {
  const [players, setPlayers] = useState(initialData?.players || []);
  // המשתנה הזה יחזיק את השחקן שנבחר לעריכה (עבור הדף האישי)
  const [selectedPlayer, setSelectedPlayer] = useState(null);
  
  const [form, setForm] = useState({ shooting: '', fitness: '', nutrition: '' });
  const [msg, setMsg] = useState('');

  useEffect(() => {
    if (!players || players.length === 0) {
      loadPlayers();
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  const loadPlayers = async () => {
    try {
      const res = await api.get('/players');
      setPlayers(res.data);
    } catch (err) {
      console.error("Error loading players:", err);
    }
  };

  // פתיחת הדף האישי של השחקן
  const openPlayerPage = (p) => {
    setSelectedPlayer(p);
    setForm({
      shooting: p.trainingPlan?.shooting || '',
      fitness: p.trainingPlan?.fitness || '',
      nutrition: p.nutritionPlan || ''
    });
  };

  const closePlayerPage = () => {
    setSelectedPlayer(null);
    setMsg('');
  };

  const submitUpdate = async () => {
    if (!selectedPlayer) return;

    try {
      const payload = {
        trainingPlan: { shooting: form.shooting, fitness: form.fitness },
        nutritionPlan: form.nutrition
      };
      
      const res = await api.post(`/propose-update/${selectedPlayer._id}`, payload);
      setMsg("✅ " + res.data.msg);
      
      // עדכון הנתונים ברקע וגם באובייקט המוצג כרגע
      loadPlayers();
      
      // משאירים את ההודעה ל-2 שניות ואז סוגרים או משאירים
      setTimeout(() => setMsg(''), 3000);
      
    } catch (err) {
      alert(err.response?.data?.msg || 'שגיאה בשמירה');
    }
  };

  return (
    <div className="dashboard-container">
      <header className="dash-header">
        <h1>פיקוד מאמנים - Mars Power Drive 🏀</h1>
        <button onClick={() => {localStorage.clear(); window.location.href='/login'}}>יציאה</button>
      </header>

      {/* --- גריד השחקנים --- */}
      <div className="roster-grid">
        {players.map(p => (
          <div key={p._id} className="player-card" style={{textAlign: 'center'}}>
            {/* תמונה עגולה בכרטיס */}
            <img 
                src={p.imageUrl} 
                alt={p.name} 
                style={{width: '80px', height: '80px', borderRadius: '50%', objectFit: 'cover', border: '3px solid #3498db'}}
                onError={(e) => {e.target.src = 'https://via.placeholder.com/80'}} 
            />
            <h3>{p.name}</h3>
            <span className="badge" style={{display: 'block', margin: '5px auto', width: 'fit-content'}}>#{p.jerseyNumber} | {p.position}</span>
            
            <button className="btn-edit" onClick={() => openPlayerPage(p)}>
              ניהול תוכנית / דף שחקן
            </button>
          </div>
        ))}
      </div>

      {/* --- דף שחקן / מודל עריכה (Overlay) --- */}
      {selectedPlayer && (
        <div className="player-modal-overlay" style={overlayStyle}>
          <div className="player-modal-content" style={modalStyle}>
            <button onClick={closePlayerPage} style={closeBtnStyle}>✖ סגור</button>
            
            <div style={{display: 'flex', gap: '20px', alignItems: 'flex-start'}}>
                {/* צד ימין: תמונה ופרטים */}
                <div style={{flex: '1', textAlign: 'center', borderLeft: '1px solid #eee', paddingLeft: '20px'}}>
                    <img 
                        src={selectedPlayer.imageUrl} 
                        alt={selectedPlayer.name}
                        style={{width: '150px', height: '150px', borderRadius: '10px', objectFit: 'cover', marginBottom: '15px'}} 
                        onError={(e) => {e.target.src = 'https://via.placeholder.com/150'}}
                    />
                    <h2>{selectedPlayer.name}</h2>
                    <p style={{fontSize: '18px'}}><strong>מספר:</strong> #{selectedPlayer.jerseyNumber}</p>
                    <p><strong>עמדה:</strong> {selectedPlayer.position}</p>
                    <p><strong>גובה:</strong> {selectedPlayer.height}</p>
                    <p><strong>תאריך לידה:</strong> {selectedPlayer.birthDate || 'לא צוין'}</p>
                </div>

                {/* צד שמאל: טופס ניהול תוכנית */}
                <div style={{flex: '2'}}>
                    <h2 style={{marginTop: 0, borderBottom: '2px solid #3498db', paddingBottom: '10px'}}>ניהול תוכנית מקצועית</h2>
                    
                    {msg && <div style={{background: '#d4edda', color: '#155724', padding: '10px', borderRadius: '5px', marginBottom: '10px'}}>{msg}</div>}

                    <div className="form-group">
                        <label>🏀 אימון קליעה:</label>
                        <input 
                            value={form.shooting} 
                            onChange={e=>setForm({...form, shooting: e.target.value})} 
                            style={inputStyle}
                        />
                    </div>

                    <div className="form-group">
                        <label>💪 כושר גופני:</label>
                        <input 
                            value={form.fitness} 
                            onChange={e=>setForm({...form, fitness: e.target.value})} 
                            style={inputStyle}
                        />
                    </div>

                    <div className="form-group">
                        <label>🥦 תזונה:</label>
                        <input 
                            value={form.nutrition} 
                            onChange={e=>setForm({...form, nutrition: e.target.value})} 
                            style={inputStyle}
                        />
                    </div>

                    <div style={{marginTop: '20px', display: 'flex', gap: '10px'}}>
                        <button onClick={submitUpdate} className="btn-save" style={{padding: '10px 20px', fontSize: '16px'}}>💾 שמור ועדכן תוכנית</button>
                        <button onClick={closePlayerPage} className="btn-cancel" style={{padding: '10px 20px', fontSize: '16px'}}>ביטול</button>
                    </div>
                </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

// סגנונות פנימיים למודל (כדי לא לגעת ב-CSS)
const overlayStyle = {
  position: 'fixed', top: 0, left: 0, right: 0, bottom: 0,
  backgroundColor: 'rgba(0,0,0,0.8)', display: 'flex', justifyContent: 'center', alignItems: 'center', zIndex: 1000
};

const modalStyle = {
  backgroundColor: 'white', padding: '30px', borderRadius: '15px', width: '90%', maxWidth: '800px',
  boxShadow: '0 5px 30px rgba(0,0,0,0.3)', position: 'relative', direction: 'rtl'
};

const closeBtnStyle = {
  position: 'absolute', top: '15px', left: '15px', background: 'none', border: 'none', fontSize: '18px', cursor: 'pointer', fontWeight: 'bold'
};

const inputStyle = {
  width: '100%', padding: '10px', fontSize: '16px', border: '1px solid #ccc', borderRadius: '5px', marginTop: '5px'
};

export default CoachDashboard;
