import React, { useState, useEffect } from 'react';
import '../css/CoachDashboard.css'; // 砖砖 拽抓 -CSS

const Dashboard = () => {
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [data, setData] = useState({ players: [], proposals: [] });
  const [user, setUser] = useState(null);
  const [view, setView] = useState('roster');

  // 驻拽爪转 转转拽转 转  (拽  注专转 )
  const manualLogout = () => {
    localStorage.clear();
    sessionStorage.clear();
    window.location.href = '/login';
  };

  useEffect(() => {
    let isMounted = true; // 注 注  专 专

    const loadData = async () => {
      try {
        console.log("转 注转 转...");
        const token = localStorage.getItem('token');

        if (!token) {
          throw new Error(" 爪 拽 转专转 ( 转专 砖)");
        }

        const response = await fetch('http://localhost:5000/api/dashboard', {
          method: 'GET',
          headers: { 
            'Content-Type': 'application/json',
            'Authorization': `Bearer ${token}` 
          }
        });

        // 1. 驻 砖转 砖专转 驻 专 -JSON
        if (!response.ok) {
          //   401/403, 拽  .  爪注 驻 转   爪专 驻.
          if (response.status === 401 || response.status === 403) {
            throw new Error("驻 转拽祝 转专转 (砖 403) -  转专 砖");
          }
          
          // 住 拽专 砖 拽住  注 拽专住转 JSON
          const errorText = await response.text();
          throw new Error(errorText || "砖 转 砖专转");
        }

        // 2.   转拽 - 专 -JSON
        const json = await response.json();
        
        if (isMounted) {
          console.log("转 转拽:", json);
          // 专 转 注转 砖转  砖转 住专
          setData({
             players: json.players || json.roster || [],
             proposals: json.proposals || []
          });
          setUser(json.user || { name: '专', role: 'unknown' });
          setLoading(false);
        }

      } catch (err) {
        console.error("砖 注:", err.message);
        if (isMounted) {
          setError(err.message);
          setLoading(false); //  注爪专 转 注  砖砖!
        }
      }
    };

    loadData();

    return () => { isMounted = false; };
  }, []);

  // --- 转爪转 注 (注 驻转专 专) ---
  if (loading) {
    return (
      <div className="coach-container" style={{textAlign: 'center', marginTop: '50px'}}>
        <h2>注 转...</h2>
        <p>  拽 转专  :</p>
        <button onClick={manualLogout} className="btn-logout" style={{background:'red'}}>
            抓   转转拽 住转 砖
        </button>
      </div>
    );
  }

  // --- 转爪转 砖 (住转 - 注转 专爪) ---
  if (error) {
    return (
      <div className="coach-container" style={{textAlign: 'center', marginTop: '50px'}}>
        <div className="error-msg">
            <h2>砖 注转 祝</h2>
            <h3>{error}</h3>
            <br/>
            <button onClick={manualLogout} className="btn-logout" style={{fontSize: '18px', padding: '10px 30px'}}>
                专 祝 转专转 (Login)
            </button>
        </div>
      </div>
    );
  }

  // --- 转爪 专砖转 (专拽 砖 转拽) ---
  return (
    <div className="coach-container">
      <header className="coach-header">
        <div className="header-title">
             <h1>Mars Power Drive </h1>
             <span>专 : <strong>{user?.name || '砖转砖'}</strong></span>
        </div>
        <button onClick={manualLogout} className="btn-logout">转转拽</button>
      </header>

      {/* 专 转 */}
      <div className="content-area">
         {/* 驻转专  */}
         <div className="tabs-container">
            <button 
                className={`tab-btn ${view==='roster'?'active':''}`} 
                onClick={()=>setView('roster')}
            >
                住 砖拽
            </button>
            
            {user?.role === 'coach' && (
              <button 
                className={`tab-btn ${view==='approvals'?'active':''}`} 
                onClick={()=>setView('approvals')}
              >
                砖专 ({data.proposals.length})
              </button>
            )}
         </div>

         {/* 爪转 专砖转 砖拽 */}
         {view === 'roster' && (
           <div className="players-grid">
             {data.players.length > 0 ? data.players.map((p, i) => (
               <div key={i} className="player-card">
                 <div className="player-name">{p.name || p.userId?.fullName || '砖拽'}</div>
                 <div className="player-info">
                    注: {p.position || '-'} <br/>
                    : {p.height || '?'} '
                 </div>
               </div>
             )) : <p> 砖拽 爪</p>}
           </div>
         )}
         
         {/* 爪转 砖专 */}
         {view === 'approvals' && (
           <div className="proposals-list">
               {data.proposals.length > 0 ? data.proposals.map((prop, i) => (
                   <div key={i} className="proposal-card">
                       <div>拽砖 注专: {prop.playerId?.name || '砖拽'}</div>
                       <div className="proposal-actions"><button>砖专</button></div>
                   </div>
               )) : <p> 拽砖转 转转</p>}
           </div>
         )}
      </div>
    </div>
  );
};

export default Dashboard;
