import React, { useState, useEffect } from 'react';

function Dashboard({ token, userRole, name, onLogout }) {
  const [view, setView] = useState('welcome'); // 爪 转爪: welcome, roster, approvals
  const [data, setData] = useState({ players: [], proposals: [] });
  const [loading, setLoading] = useState(false);

  // 驻拽爪 砖驻转 转 砖专转
  const fetchData = async () => {
    setLoading(true);
    try {
      const response = await fetch('http://localhost:5000/api/dashboard', {
        headers: { Authorization: token }
      });
      const result = await response.json();
      
      //    - 拽 砖拽 爪注转.  砖拽 - 拽 专拽 转 转 砖
      if (userRole === 'coach') {
        setData({ players: result.players, proposals: result.proposals });
      } else {
        // 转 拽专 砖 砖拽
        setData({ players: [result], proposals: [] });
      }
    } catch (error) {
      console.error("Error fetching data:", error);
      alert("砖 注转 转");
    }
    setLoading(false);
  };

  // 注转 转 专砖转
  useEffect(() => {
    fetchData();
  }, []);

  // --- 住转 (Styles) ---
  const containerStyle = { padding: '20px', fontFamily: 'Arial, sans-serif' };
  const headerStyle = { display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '20px', borderBottom: '2px solid #ccc', paddingBottom: '10px' };
  const buttonStyle = { padding: '10px 20px', margin: '0 10px', cursor: 'pointer', backgroundColor: '#007bff', color: 'white', border: 'none', borderRadius: '5px', fontSize: '16px' };
  const logoutStyle = { ...buttonStyle, backgroundColor: '#dc3545' };
  const activeButtonStyle = { ...buttonStyle, backgroundColor: '#0056b3', fontWeight: 'bold' };

  return (
    <div style={containerStyle}>
      {/* 转专转 注 */}
      <div style={headerStyle}>
        <h1>Mars Power Drive </h1>
        <div>
          <h3>砖, {name} ({userRole === 'coach' ? '' : '砖拽'})</h3>
          <button onClick={onLogout} style={logoutStyle}>转转拽</button>
        </div>
      </div>

      {/* 转驻专  - 驻转专 转! */}
      <div style={{ marginBottom: '20px', textAlign: 'center' }}>
        <button 
          onClick={() => setView('roster')} 
          style={view === 'roster' ? activeButtonStyle : buttonStyle}
        >
           住 砖拽 (Roster)
        </button>
        
        {userRole === 'coach' && (
          <button 
            onClick={() => setView('approvals')} 
            style={view === 'approvals' ? activeButtonStyle : buttonStyle}
          >
            锔 砖专 转 ({data.proposals.length})
          </button>
        )}
      </div>

      {/* 专 转 转祝 */}
      <div style={{ border: '1px solid #ddd', padding: '20px', borderRadius: '8px', backgroundColor: '#f9f9f9' }}>
        
        {loading && <p>注 转...</p>}

        {/* 转爪转 专专转  */}
        {view === 'welcome' && !loading && (
          <div style={{ textAlign: 'center' }}>
            <h2>专  注专转 </h2>
            <p> 专 转 驻砖专转 转驻专 注.</p>
          </div>
        )}

        {/* 转爪转 住 砖拽 */}
        {view === 'roster' && (
          <div>
            <h2>住 砖拽</h2>
            <ul style={{ listStyleType: 'none', padding: 0 }}>
              {data.players.map((player) => (
                <li key={player._id} style={{ background: 'white', margin: '10px 0', padding: '15px', border: '1px solid #ddd', borderRadius: '5px' }}>
                  <strong>{player.userId?.fullName || '砖拽  '}</strong> - : {player.height}m | 注: {player.position}
                  <br />
                  <small>转转 砖专: {player.trainingPlan?.fitness || ' 专'}</small>
                </li>
              ))}
            </ul>
          </div>
        )}

        {/* 转爪转 砖专 (专拽 ) */}
        {view === 'approvals' && (
          <div>
            <h2>爪注转 砖专 (拽 3/3)</h2>
            {data.proposals.length === 0 ? <p> 爪注转 转转 砖专.</p> : (
              <ul style={{ listStyleType: 'none', padding: 0 }}>
                {data.proposals.map((prop) => (
                  <li key={prop._id} style={{ background: '#fff3cd', margin: '10px 0', padding: '15px', border: '1px solid #ffeeba', borderRadius: '5px' }}>
                    <strong>注专 砖拽:</strong> {prop.playerId?.userId?.fullName} <br />
                    <strong>爪注 注":</strong> {prop.proposedBy?.fullName} <br />
                    <strong>砖 爪注:</strong> {JSON.stringify(prop.newPlanData)} <br />
                    <strong>住住 砖专:</strong> {prop.approvals.length}/3
                    <br />
                    <button style={{ ...buttonStyle, backgroundColor: '#28a745', marginTop: '10px' }}>
                      砖专 爪注 (Approve)
                    </button>
                  </li>
                ))}
              </ul>
            )}
          </div>
        )}
      </div>
    </div>
  );
}

export default Dashboard;
