import React, { useState } from 'react';
import api from '../api/axios';
import '../css/PlayerModal.css';
import ShootingModal from './ShootingModal'; // וודא שהנתיב נכון

const PlayerModal = ({ player, onClose, onSave }) => {
  const [form, setForm] = useState({
    shooting: player.trainingPlan?.shooting || '300 זריקות', // הסיכום (למשל "320 זריקות")
    shootingDrills: player.trainingPlan?.shootingDrills || '', // הפירוט המלא
    fitness: player.trainingPlan?.fitness || '',
    nutrition: player.nutritionPlan || ''
  });
  
  const [msg, setMsg] = useState('');
  const [showDrills, setShowDrills] = useState(false);

  const calculateAge = (dateString) => {
    if (!dateString || dateString === 'לא צוין') return '---';
    const parts = dateString.split('/');
    if (parts.length !== 3) return '---';
    const birthDate = new Date(parts[2], parts[1] - 1, parts[0]);
    const today = new Date();
    let age = today.getFullYear() - birthDate.getFullYear();
    const m = today.getMonth() - birthDate.getMonth();
    if (m < 0 || (m === 0 && today.getDate() < birthDate.getDate())) age--;
    return age;
  };

  const handleSubmit = async () => {
    try {
      const payload = {
        trainingPlan: { 
            shooting: form.shooting,       // שולח את הסיכום המעודכן לשרת
            shootingDrills: form.shootingDrills, 
            fitness: form.fitness 
        },
        nutritionPlan: form.nutrition
      };
      
      const res = await api.post(`/propose-update/${player._id}`, payload);
      setMsg("✅ " + res.data.msg);
      if (onSave) onSave(); // רענון נתונים באבא אם צריך
      setTimeout(() => setMsg(''), 2000);
    } catch (err) {
      alert(err.response?.data?.msg || 'שגיאה בשמירה');
    }
  };

  // --- התיקון החשוב נמצא כאן ---
  // הפונקציה מקבלת כעת שני ערכים: את הפירוט (drills) ואת הסיכום (summary)
  const handleDrillsUpdate = (newDrills, newSummary) => {
      setForm({
          ...form, 
          shootingDrills: newDrills, // עדכון הטקסט המפורט
          shooting: newSummary       // עדכון הכותרת (למשל "450 זריקות")
      });
      setShowDrills(false); // סגירת החלון
  };

  return (
    <div className="player-modal-overlay">
      <div className="player-modal-content">
        <button onClick={onClose} className="close-btn">✖ סגור</button>
        
        <div className="modal-body">
            {/* צד ימין - פרטי שחקן */}
            <div className="player-details-side">
                <img src={player.imageUrl} alt={player.name} className="player-large-img" onError={(e) => {e.target.src = 'https://via.placeholder.com/150'}} />
                <h2 className="player-name-red">{player.name}</h2>
                <div className="details-list">
                    <div className="detail-row"><strong>📍 עמדה:</strong> <span>{player.position}</span></div>
                    <div className="detail-row"><strong>👕 מספר גופייה:</strong> <span>#{player.jerseyNumber}</span></div>
                    <div className="detail-row"><strong>📏 גובה:</strong> <span>{player.height} מ'</span></div>
                    <div className="detail-row"><strong>📅 תאריך לידה:</strong> <span>{player.birthDate}</span></div>
                    <div className="detail-row"><strong>🎂 גיל:</strong> <span>{calculateAge(player.birthDate)}</span></div>
                </div>
            </div>

            {/* צד שמאל - טופס עריכה */}
            <div className="form-side">
                <h2 className="form-title">📋 תוכנית עבודה מקצועית</h2>
                {msg && <div style={{background: '#d4edda', color: '#155724', padding: '15px', borderRadius: '8px', marginBottom: '20px', fontWeight: 'bold'}}>{msg}</div>}

                <div className="form-group">
                    <label className="form-label">🏀 אימון קליעה (לחץ לפירוט ועריכה):</label>
                    <div 
                        onClick={() => setShowDrills(true)}
                        style={{
                            cursor: 'pointer', 
                            border: '2px dashed #e67e22', 
                            padding: '10px', 
                            borderRadius: '8px', 
                            backgroundColor: '#fffdf0', 
                            color: '#e67e22', 
                            fontWeight: 'bold',
                            display: 'flex',
                            justifyContent: 'space-between',
                            alignItems: 'center'
                        }}
                    >
                        {/* כאן מוצג הסיכום שמתעדכן (form.shooting) */}
                        <span>{form.shooting}</span>
                        <span style={{fontSize: '0.9em', color: '#888'}}>(לחץ לעריכה מפורטת)</span>
                    </div>
                </div>

                <div className="form-group">
                    <label className="form-label">💪 כושר גופני:</label>
                    <input className="form-input" value={form.fitness} onChange={e=>setForm({...form, fitness: e.target.value})} />
                </div>

                <div className="form-group">
                    <label className="form-label">🥦 תזונה:</label>
                    <input className="form-input" value={form.nutrition} onChange={e=>setForm({...form, nutrition: e.target.value})} />
                </div>

                <div className="btn-actions">
                    <button onClick={handleSubmit} className="btn-save">💾 שמור עדכונים לשרת</button>
                    <button onClick={onClose} className="btn-cancel">ביטול</button>
                </div>
            </div>
        </div>
      </div>

      {/* החלון הקופץ */}
      <ShootingModal 
        isOpen={showDrills}
        onClose={() => setShowDrills(false)}
        drills={form.shootingDrills}
        onSave={handleDrillsUpdate} // שליחת הפונקציה המתוקנת
        isReadOnly={false} 
      />
    </div>
  );
};

export default PlayerModal;
