import React, { useState, useEffect } from 'react';
import '../css/CoachDashboard.css'; // 砖砖 拽抓 -CSS 砖爪专

const Dashboard = () => {
  const [view, setView] = useState('roster'); 
  const [data, setData] = useState({ players: [], proposals: [] });
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // --- 驻拽爪转 爪 (拽  注专转 ) ---
  const handleLogout = () => {
    localStorage.removeItem('token'); // 拽 住驻爪驻转
    localStorage.clear(); // 拽  转专 
    sessionStorage.clear();
    window.location.href = '/login'; // 注专  祝 转专转
  };

  useEffect(() => {
    let isMounted = true; // 注 注  专 专 专 (注 专爪)

    const fetchData = async () => {
      try {
        const token = localStorage.getItem('token');

        // 1.   拽  -  转住 驻 驻转 砖专转
        if (!token) {
          if (isMounted) handleLogout();
          return;
        }

        const response = await fetch('http://localhost:5000/api/dashboard', {
          method: 'GET',
          headers: {
            'Content-Type': 'application/json',
            'Authorization': `Bearer ${token}`
          }
        });

        // 2. 驻 砖转 砖专转 (401/403) 驻 住 拽专转 JSON
        if (!response.ok) {
          if (response.status === 401 || response.status === 403) {
            console.warn("Session expired. Logging out...");
            handleLogout(); // 注祝 转 砖转砖 
            return;
          }
          
          // 拽专转 注转 砖 拽住 (注 转 拽专住 砖 Unexpected token I)
          const errorText = await response.text();
          throw new Error(errorText || '砖转 砖专转 转');
        }

        // 3. 专拽   转拽 - 专 -JSON
        const result = await response.json();

        if (isMounted) {
          setData({ 
            players: result.players || result.roster || [], 
            proposals: result.proposals || [] 
          });
          setUser(result.user || { name: '砖转砖', role: 'guest' });
          setLoading(false);
        }

      } catch (err) {
        console.error("Dashboard Error:", err);
        if (isMounted) {
          setError(err.message);
          setLoading(false);
        }
      }
    };

    fetchData();

    // 驻拽爪转 拽 (Cleanup)
    return () => { isMounted = false; };
  }, []);

  // --- 转爪转 注 ---
  if (loading) return <div className="coach-container"><h2 className="loading-msg">注 转...</h2></div>;

  // --- 转爪转 砖 (住转,  拽驻爪转) ---
  if (error) {
    return (
      <div className="coach-container">
        <div className="error-msg">
            <h2>砖 注转 转</h2>
            <p>{error}</p>
            <button onClick={handleLogout} className="btn-logout">抓  转专转 砖</button>
        </div>
      </div>
    );
  }

  // --- 转爪 专砖转 ---
  return (
    <div className="coach-container">
      
      {/* 转专转 */}
      <header className="coach-header">
        <div className="header-title">
             <h1>Mars Power Drive </h1>
             <span className="header-subtitle">注专转  ({user?.role === 'coach' ? '' : '砖拽'})</span>
        </div>
        
        <div className="user-actions">
            <span>砖, <strong>{user?.name || '专'}</strong></span>
            <button onClick={handleLogout} className="btn-logout">转转拽</button>
        </div>
      </header>

      {/*  (爪 专拽  砖转砖  , 专转 专拽 住) */}
      <div className="tabs-container">
        <button 
            className={`tab-btn ${view === 'roster' ? 'active' : ''}`} 
            onClick={() => setView('roster')}
        >
             住 砖拽
        </button>
        
        {user?.role === 'coach' && (
            <button 
                className={`tab-btn ${view === 'approvals' ? 'active' : ''}`} 
                onClick={() => setView('approvals')}
            >
                锔 砖专 ({data.proposals.length})
            </button>
        )}
      </div>

      {/* 转 */}
      <main className="content-area">
        
        {/* 住 */}
        {view === 'roster' && (
            <div>
                <h2 className="section-title">专砖转 砖拽</h2>
                {data.players.length === 0 ? <p> 砖拽 爪.</p> : (
                    <div className="players-grid">
                        {data.players.map((p, i) => (
                            <div key={i} className="player-card">
                                <div className="player-name">{p.name || p.userId?.fullName || '砖拽'}</div>
                                <div className="player-info">
                                    注: {p.position || '-'} | : {p.height || '?'}
                                </div>
                            </div>
                        ))}
                    </div>
                )}
            </div>
        )}

        {/* 砖专 (专拽 ) */}
        {view === 'approvals' && user?.role === 'coach' && (
            <div>
                <h2 className="section-title">拽砖转 砖专</h2>
                {data.proposals.length === 0 ? <p> 拽砖转 砖转.</p> : (
                    <div className="proposals-list">
                        {data.proposals.map((prop, i) => (
                            <div key={i} className="proposal-card">
                                <div>拽砖 注专 {prop.playerId?.name || '砖拽'}</div>
                                <div className="proposal-actions">
                                    <button>砖专</button>
                                </div>
                            </div>
                        ))}
                    </div>
                )}
            </div>
        )}

      </main>
    </div>
  );
};

export default Dashboard;
