import React, { useState, useEffect } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import api from '../api/axios'; 
import '../css/FullPage.css';

const ShootingPage = () => {
  const navigate = useNavigate();
  const location = useLocation();
  
  const { text: initialText, title: initialTitle, isReadOnly, playerId, returnPath } = location.state || {};
  
  const [text, setText] = useState(initialText || '');
  const [title, setTitle] = useState(initialTitle || '');

  // --- פונקציה לחישוב אוטומטי של סך הזריקות ---
  const calculateTotalShots = (inputText) => {
    // הביטוי הרגולרי מחפש מספרים שמופיעים לפני המילה "זריקות"
    // דוגמה: תופס את ה-50 מתוך "50 זריקות" ואת ה-10 מתוך "10 זריקות"
    const regex = /(\d+)\s+זריקות/g;
    let match;
    let total = 0;

    // רץ על כל הטקסט ומוצא את כל ההתאמות
    while ((match = regex.exec(inputText)) !== null) {
        total += parseInt(match[1], 10);
    }

    return total;
  };

  // --- פונקציה שמטפלת בשינוי הטקסט ---
  const handleTextChange = (e) => {
      const newText = e.target.value;
      setText(newText);

      // חישוב הסכום בזמן אמת
      const total = calculateTotalShots(newText);
      
      // אם נמצאו זריקות, נעדכן את הכותרת אוטומטית
      if (total > 0) {
          setTitle(`${total} זריקות`);
      }
  };

  const handleBack = () => {
      if (returnPath && playerId) {
          navigate(returnPath, { state: { restorePlayerId: playerId } });
      } else {
          navigate(-1);
      }
  };

  const handleSave = async () => {
    if (!playerId) {
        alert("שגיאה: חסר מזהה שחקן");
        return;
    }

    try {
      // משיכת הנתונים העדכניים מהשרת למניעת דריסה
      const response = await api.get('/players'); 
      const currentPlayer = response.data.find(p => p._id === playerId);

      if (!currentPlayer) {
          alert("שגיאה: השחקן לא נמצא בשרת");
          return;
      }

      const currentPlan = currentPlayer.trainingPlan || {};

      // עדכון הנתונים
      const updatedTrainingPlan = {
          ...currentPlan,        
          shootingDrills: text,  
          shooting: title        
      };

      await api.post(`/propose-update/${playerId}`, {
        trainingPlan: updatedTrainingPlan
      });
      
      alert(`✅ נשמר בהצלחה!\nכותרת האימון עודכנה ל: ${title}`);
      handleBack(); 
    } catch (err) {
      console.error(err);
      alert('❌ שגיאה בשמירה');
    }
  };

  return (
    <div className="full-page-container">
      <div className="page-header header-shooting">
        <h1 className="page-title">🏀 עריכת אימון קליעה</h1>
        <button className="btn-back" onClick={handleBack}>חזור ↩</button>
      </div>

      <div className="content-box">
        {isReadOnly ? (
          <>
            <h2 style={{borderBottom:'2px solid #eee', paddingBottom:'10px'}}>{title}</h2>
            <div className="text-display">{text}</div>
          </>
        ) : (
          <>
            <div style={{marginBottom: '20px'}}>
                <label style={{fontWeight:'bold', display:'block', marginBottom:'5px', color:'#333'}}>כותרת האימון (מתעדכנת אוטומטית):</label>
                <input 
                    type="text"
                    value={title}
                    onChange={(e) => setTitle(e.target.value)}
                    style={{
                        width: '100%', 
                        padding: '10px', 
                        fontSize: '1.2rem', 
                        borderRadius: '5px', 
                        border: '1px solid #e67e22', 
                        background: '#fffdf0',
                        fontWeight: 'bold',
                        color: '#d35400'
                    }}
                />
                <small style={{color:'#666'}}>* הכותרת מחושבת אוטומטית לפי הטקסט למטה, אך ניתן לשנות ידנית.</small>
            </div>

            <label style={{fontWeight:'bold', display:'block', marginBottom:'5px', color:'#333'}}>פירוט התרגילים (שנה מספרים והכותרת תשתנה):</label>
            <textarea 
              className="edit-textarea" 
              value={text} 
              onChange={handleTextChange} // כאן הפעלנו את הפונקציה החדשה
              placeholder="לדוגמה: 1. 60 זריקות..."
            />
            <div className="action-buttons">
              <button className="btn-save" onClick={handleSave}>💾 שמור שינויים ועדכן שחקן</button>
            </div>
          </>
        )}
      </div>
    </div>
  );
};

export default ShootingPage;
