import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import CoachDashboard from './CoachDashboard';
import PlayerDashboard from './PlayerDashboard';
import '../css/Dashboard.css';

const Dashboard = () => {
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [userRole, setUserRole] = useState(null);
  const [userData, setUserData] = useState(null);
  const navigate = useNavigate();

  // פונקציה להתנתקות ומחיקת המידע מהדפדפן
  const handleLogout = () => {
    localStorage.clear();
    navigate('/login');
  };

  useEffect(() => {
    const identifyUser = async () => {
      try {
        const token = localStorage.getItem('token');
        
        // אם אין טוקן בכלל - זורקים ללוגין
        if (!token) {
            navigate('/login');
            return;
        }

        // --- תיקון: מצב DEMO ---
        // אם המשתמש נכנס כ-demo, אנחנו מדלגים על השרת ומציגים נתונים כאילו
        if (token === 'demo-token-123') {
            const mockData = {
                user: { name: 'מאמן הדגמה', role: 'coach' },
                players: [
                    { name: 'דני אבדיה', position: 'SF', height: '2.06' },
                    { name: 'ים מדר', position: 'PG', height: '1.90' },
                    { name: 'לברון ג׳יימס', position: 'SF', height: '2.06' }
                ],
                proposals: [
                    { playerId: { name: 'נועם יעקב' }, details: 'בקשה לשינוי עמדה' }
                ]
            };
            
            // דימוי של טעינה קצרה (חצי שנייה)
            setTimeout(() => {
                setUserRole(mockData.user.role);
                setUserData(mockData);
                setLoading(false);
            }, 500);
            return;
        }

        // --- קוד רגיל (חיבור לשרת אמיתי) ---
        // החלק הזה יפעל רק אם יהיה לך שרת backend בעתיד
        const response = await fetch('http://localhost:5000/api/dashboard', {
          headers: { 'Authorization': `Bearer ${token}` }
        });

        if (!response.ok) {
            if (response.status === 401 || response.status === 403) {
                handleLogout(); // הטוקן פג תוקף
                return;
            }
            throw new Error("שגיאה בטעינת הנתונים מהשרת");
        }

        const result = await response.json();
        const role = result.user?.role || 'player'; 
        
        setUserRole(role);
        setUserData(result);
        setLoading(false);

      } catch (err) {
        console.error(err);
        setError(err.message);
        setLoading(false);
      }
    };

    identifyUser();
  }, [navigate]);

  // --- תצוגת טעינה ---
  if (loading) {
    return (
        <div className="loader-container">
            <div className="loader"></div>
            <h3>טוען נתונים...</h3>
        </div>
    );
  }

  // --- תצוגת שגיאה ---
  if (error) {
    return (
      <div className="dashboard-container center-content">
        <h2 className="error-text">שגיאה: {error}</h2>
        <button onClick={handleLogout} className="btn-logout">חזור למסך התחברות</button>
      </div>
    );
  }

  // --- ניתוב לפי סוג המשתמש (מאמן או שחקן) ---
  return userRole === 'coach' 
    ? <CoachDashboard initialData={userData} onLogout={handleLogout} />
    : <PlayerDashboard initialData={userData} onLogout={handleLogout} />;
};

export default Dashboard;
