import React, { useState } from 'react';
import '../css/Dashboard.css';

const CoachDashboard = ({ initialData, onLogout }) => {
  const [view, setView] = useState('roster');
  const [players] = useState(initialData.players || initialData.roster || []);
  const [proposals] = useState(initialData.proposals || []);

  return (
    <div className="dashboard-container">
      <div className="dashboard-header">
        <div>
            <h1>לוח בקרה - מאמן 📋</h1>
            <span className="welcome-text">שלום, {initialData.user?.name}</span>
        </div>
        <button onClick={onLogout} className="btn-logout">התנתק</button>
      </div>

      <div className="nav-tabs">
        <button 
            className={`nav-btn ${view === 'roster' ? 'active' : ''}`} 
            onClick={() => setView('roster')}
        >
            סגל שחקנים
        </button>
        <button 
            className={`nav-btn ${view === 'approvals' ? 'active' : ''}`} 
            onClick={() => setView('approvals')}
        >
            אישורים ({proposals.length})
        </button>
      </div>

      <div className="content-box">
        {view === 'roster' && (
            <div className="players-grid">
                {players.length === 0 ? <p>אין שחקנים להצגה</p> : players.map((p, i) => (
                    <div key={i} className="card-item player-card">
                        <div className="avatar">{p.name?.[0] || 'P'}</div>
                        <div>
                            <strong>{p.name || p.userId?.fullName}</strong>
                            <div className="sub-text">עמדה: {p.position || '-'}</div>
                        </div>
                    </div>
                ))}
            </div>
        )}

        {view === 'approvals' && (
            <div className="proposals-list">
                {proposals.length === 0 ? <p>אין בקשות ממתינות ✅</p> : proposals.map((prop, i) => (
                    <div key={i} className="card-item proposal-item">
                        <strong>בקשה מ: {prop.playerId?.name || 'שחקן'}</strong>
                        <p>{prop.details || 'פרטי בקשה...'}</p>
                        <button className="btn-primary">אשר בקשה</button>
                    </div>
                ))}
            </div>
        )}
      </div>
    </div>
  );
};

export default CoachDashboard;
