import React, { useState } from 'react';
import '../css/Dashboard.css';

const CoachDashboard = ({ initialData, onLogout }) => {
  const [view, setView] = useState('roster');
  const players = initialData.players || initialData.roster || [];
  const proposals = initialData.proposals || [];

  return (
    <div className="dashboard-container">
      <div className="dashboard-header">
        <h1>   <small>砖, {initialData.user?.name}</small></h1>
        <button onClick={onLogout} className="btn-logout">爪</button>
      </div>

      <div className="nav-tabs">
        <button className={`nav-btn ${view === 'roster' ? 'active' : ''}`} onClick={() => setView('roster')}>住 砖拽</button>
        <button className={`nav-btn ${view === 'approvals' ? 'active' : ''}`} onClick={() => setView('approvals')}>砖专 ({proposals.length})</button>
      </div>

      <div className="content-box">
        {view === 'roster' && (
            <div className="players-grid">
                {players.map((p, i) => (
                    <div key={i} className="card-item player-card">
                        <strong>{p.name || p.userId?.fullName}</strong>
                        <div>注: {p.position || '-'}</div>
                    </div>
                ))}
            </div>
        )}
        {view === 'approvals' && <div>{proposals.length === 0 ? ' 拽砖转' : '专砖转 拽砖转...'}</div>}
      </div>
    </div>
  );
};
export default CoachDashboard;
