import React, { useEffect, useState } from 'react';
import { useNavigate, useLocation } from 'react-router-dom'; // הוספנו useLocation
import '../css/PlayerModal.css'; 
import '../css/CoachDashboard.css'; 
// אין צורך לייבא יותר את המודלים!

const PlayerDashboard = () => {
  const [player, setPlayer] = useState(null);
  const navigate = useNavigate();
  const location = useLocation(); // כדי לתפוס חזרות עם עדכונים

  useEffect(() => {
    const storedUser = localStorage.getItem('user');
    if (storedUser) {
      let userData = JSON.parse(storedUser);
      
      // אם חזרנו מדף עריכה עם עדכונים (למשל קליעה)
      if (location.state?.updatedDrills) {
        userData.trainingPlan.shootingDrills = location.state.updatedDrills;
        userData.trainingPlan.shooting = location.state.updatedSummary;
        localStorage.setItem('user', JSON.stringify(userData));
        // הערה: במערכת אמיתית היינו מרעננים מול השרת
      }
      
      setPlayer(userData);
    } else {
      navigate('/login');
    }
  }, [navigate, location.state]);

  const calculateAge = (dateString) => {
    if (!dateString || dateString === 'לא צוין') return '---';
    const parts = dateString.split('/');
    if (parts.length !== 3) return '---';
    const birthDate = new Date(parts[2], parts[1] - 1, parts[0]);
    const today = new Date();
    let age = today.getFullYear() - birthDate.getFullYear();
    const m = today.getMonth() - birthDate.getMonth();
    if (m < 0 || (m === 0 && today.getDate() < birthDate.getDate())) age--;
    return age;
  };

  if (!player) return <div style={{textAlign:'center', marginTop:'50px'}}>טוען נתונים...</div>;

  return (
    <div className="coach-container">
      <header className="coach-header">
         <div className="header-title"><h1>האזור האישי - Mars Power Drive 🏀</h1></div>
         <button className="btn-logout" onClick={() => {localStorage.clear(); window.location.href='/login'}}>יציאה</button>
      </header>

      <div style={{display:'flex', justifyContent:'center', marginTop:'20px'}}>
        <div className="player-modal-content" style={{width:'100%', maxWidth:'1000px', boxShadow:'none', border:'1px solid #ddd', position: 'static'}}>
            <div className="modal-body">
                {/* צד ימין - פרטים */}
                <div className="player-details-side">
                    <img src={player.imageUrl} alt={player.name} className="player-large-img" onError={(e) => {e.target.src = 'https://via.placeholder.com/150'}} />
                    <h2 className="player-name-red">{player.name}</h2>
                    <div className="details-list">
                        <p><strong>📍 עמדה:</strong> {player.position}</p>
                        <p><strong>👕 מספר:</strong> {player.jerseyNumber}</p>
                        <p><strong>📏 גובה:</strong> {player.height} מ'</p>
                        <p><strong>🎂 גיל:</strong> {calculateAge(player.birthDate)}</p>
                    </div>
                </div>

                {/* צד שמאל - תוכניות */}
                <div className="form-side">
                    <h2 className="form-title">📋 התוכנית המקצועית שלי</h2>
                    
                    {/* אימון קליעה - מעבר לדף */}
                    <div className="form-group">
                        <label className="form-label">🏀 אימון קליעה:</label>
                        <div 
                            onClick={() => navigate('/shooting', { 
                              state: { 
                                drills: player.trainingPlan?.shootingDrills, 
                                isReadOnly: true 
                              } 
                            })}
                            className="clickable-input orange-border"
                        >
                             <span>{player.trainingPlan?.shooting || 'אין נתונים'}</span>
                             <span style={{fontSize: '0.8rem'}}>פתח דף מלא 👈</span>
                        </div>
                    </div>

                    {/* כושר גופני - מעבר לדף */}
                    <div className="form-group">
                        <label className="form-label">💪 כושר גופני:</label>
                        <div 
                            onClick={() => navigate('/fitness', { 
                              state: { planText: player.trainingPlan?.fitnessDrills } 
                            })} 
                            className="clickable-input green-border"
                        >
                            <span>{player.trainingPlan?.fitness || 'אין נתונים'}</span>
                            <span style={{fontSize: '0.8rem'}}>פתח דף מלא 👁️</span>
                        </div>
                    </div>

                    {/* תזונה - מעבר לדף */}
                    <div className="form-group">
                        <label className="form-label">🥦 תזונה:</label>
                        <div 
                            onClick={() => navigate('/nutrition', { 
                              state: { menuText: player.nutritionMenu } 
                            })} 
                            className="clickable-input blue-border"
                        >
                            <span>{player.nutritionPlan || 'תפריט מאוזן'}</span>
                            <span style={{fontSize: '0.8rem'}}>פתח דף מלא 🥗</span>
                        </div>
                    </div>
                    
                    <div style={{marginTop: '30px', textAlign: 'center', color: '#7f8c8d', borderTop: '1px solid #eee', paddingTop: '10px'}}>
                        <em>* לשינוי תוכנית יש לפנות לצוות המקצועי</em>
                    </div>
                </div>
            </div>
        </div>
      </div>
    </div>
  );
};

export default PlayerDashboard;
