import React, { useState, useEffect } from 'react';
import '../css/WelcomeMessage.css';

const WelcomeMessage = ({ text }) => {
  const [isVisible, setIsVisible] = useState(true);

  useEffect(() => {
    // טיימר להעלמת ההודעה אחרי 3 שניות (3000 מילי-שניות)
    const timer = setTimeout(() => {
      setIsVisible(false);
    }, 4000);

    // ניקוי הטיימר בעת יציאה מהרכיב למניעת זליגת זיכרון
    return () => clearTimeout(timer);
  }, []);

  if (!isVisible) return null;

  return (
    <div className="welcome-toast-overlay">
      <div className="welcome-toast-box">
        {text}
      </div>
    </div>
  );
};

export default WelcomeMessage;
