import React, { useState } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import api from '../api/axios'; 
import '../css/FullPage.css';

const ShootingPage = () => {
  const navigate = useNavigate();
  const location = useLocation();
  
  // שליפת הנתונים שהועברו מהדשבורד
  const { text: initialText, isReadOnly, playerId } = location.state || {};
  
  const [text, setText] = useState(initialText || '');

  const handleSave = async () => {
    if (!playerId) {
      alert('שגיאה: לא נמצא מזהה שחקן, לא ניתן לשמור.');
      return;
    }

    try {
      // עדכון בשרת
      await api.post(`/propose-update/${playerId}`, {
        trainingPlan: {
           shootingDrills: text 
        }
      });
      
      alert('✅ התוכנית נשמרה בשרת בהצלחה!');
      navigate(-1); // חזרה לדף הקודם
    } catch (err) {
      console.error(err);
      alert('❌ שגיאה בשמירה, נסה שוב.');
    }
  };

  return (
    <div className="full-page-container">
      <div className="page-header header-shooting">
        <h1 className="page-title">🏀 אימון קליעה - עריכה מלאה</h1>
        <button className="btn-back" onClick={() => navigate(-1)}>חזור ללא שמירה ↩</button>
      </div>

      <div className="content-box">
        {isReadOnly ? (
          <div className="text-content">{text}</div>
        ) : (
          <>
            <textarea 
              className="edit-textarea" 
              value={text} 
              onChange={(e) => setText(e.target.value)}
              placeholder="כתוב כאן את תוכנית האימון..."
            />
            <div className="action-buttons">
              <button className="btn-save" onClick={handleSave}>💾 שמור שינויים בשרת</button>
            </div>
          </>
        )}
      </div>
    </div>
  );
};

export default ShootingPage;
