import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

function Dashboard() {
  const [view, setView] = useState('welcome'); // welcome, roster, approvals
  const [data, setData] = useState({ players: [], proposals: [] });
  const [loading, setLoading] = useState(true);
  const [user, setUser] = useState(null); // 砖专 转 驻专 砖转砖 
  const navigate = useNavigate();

  // --- 驻拽爪转 转转拽转 ---
  const handleLogout = () => {
    localStorage.clear();
    window.location.href = '/login';
  };

  // --- 砖驻转 转 ---
  useEffect(() => {
    const fetchData = async () => {
      try {
        const token = localStorage.getItem('token');
        
        if (!token) {
          handleLogout();
          return;
        }

        // 1. 砖 注 Bearer (砖 !)
        const response = await fetch('http://localhost:5000/api/dashboard', {
          method: 'GET',
          headers: { 
            'Content-Type': 'application/json',
            'Authorization': `Bearer ${token}` 
          }
        });

        // 2. 驻 砖转 (注 拽专住)
        if (!response.ok) {
          if (response.status === 401 || response.status === 403) {
            console.warn("拽  转拽 - 转转拽...");
            handleLogout();
            return;
          }
          // 拽专转 砖 拽住   拽专住
          const errorText = await response.text();
          throw new Error(errorText);
        }

        // 3. 专 -JSON
        const result = await response.json();
        
        // 住专 转 驻 住 砖转砖 砖专 砖专转
        //   砖砖专转 专  砖 userRole  similar
        //  转 转   砖专转 拽爪 砖
        if (result.userRole === 'coach' || result.role === 'coach') {
             setData({ players: result.players || [], proposals: result.proposals || [] });
        } else {
             setData({ players: result.roster || [result], proposals: [] });
        }
        
        // 砖专转 驻专 砖转砖 转爪
        setUser(result.user || { name: '砖转砖', role: result.userRole });

      } catch (error) {
        console.error("Error fetching data:", error);
        //  专拽 alert   转拽注 转 砖拽
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  // --- 住转 ---
  const containerStyle = { padding: '20px', fontFamily: 'Arial, sans-serif' };
  const headerStyle = { display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '20px', borderBottom: '2px solid #ccc', paddingBottom: '10px' };
  const buttonStyle = { padding: '10px 20px', margin: '0 10px', cursor: 'pointer', backgroundColor: '#007bff', color: 'white', border: 'none', borderRadius: '5px', fontSize: '16px' };
  const logoutStyle = { ...buttonStyle, backgroundColor: '#dc3545' };
  const activeButtonStyle = { ...buttonStyle, backgroundColor: '#0056b3', fontWeight: 'bold' };

  return (
    <div style={containerStyle}>
      {/* 转专转 注 */}
      <div style={headerStyle}>
        <h1 style={{margin: 0}}>Mars Power Drive </h1>
        <div>
          {/* 爪 转 砖 专拽  注 注 */}
          <h3>砖, {user?.name || '注...'}</h3>
          <button onClick={handleLogout} style={logoutStyle}>转转拽</button>
        </div>
      </div>

      {/* 转驻专  */}
      <div style={{ marginBottom: '20px', textAlign: 'center' }}>
        <button 
          onClick={() => setView('roster')} 
          style={view === 'roster' ? activeButtonStyle : buttonStyle}
        >
           住 砖拽
        </button>
        
        {/* 爪 驻转专 砖专 专拽    */}
        {user?.role === 'coach' && (
          <button 
            onClick={() => setView('approvals')} 
            style={view === 'approvals' ? activeButtonStyle : buttonStyle}
          >
            锔 砖专 转 ({data.proposals.length})
          </button>
        )}
      </div>

      {/* 专 转 */}
      <div style={{ border: '1px solid #ddd', padding: '20px', borderRadius: '8px', backgroundColor: '#f9f9f9', minHeight: '300px' }}>
        
        {loading && <h2 style={{textAlign: 'center'}}>注 转...</h2>}

        {!loading && view === 'welcome' && (
          <div style={{ textAlign: 'center' }}>
            <h2>专  注专转 </h2>
            <p>专 驻砖专转 转驻专 注.</p>
          </div>
        )}

        {!loading && view === 'roster' && (
          <div>
            <h2>住 砖拽</h2>
            {data.players.length === 0 ? <p> 砖拽 爪</p> : (
              <ul style={{ listStyleType: 'none', padding: 0 }}>
                {data.players.map((player, index) => (
                  <li key={player._id || index} style={{ background: 'white', margin: '10px 0', padding: '15px', border: '1px solid #ddd', borderRadius: '5px' }}>
                    <strong>{player.name || player.userId?.fullName || '砖拽'}</strong> 
                    {player.position && ` - 注: ${player.position}`}
                  </li>
                ))}
              </ul>
            )}
          </div>
        )}

        {!loading && view === 'approvals' && (
          <div>
            <h2>爪注转 砖专</h2>
            {data.proposals.length === 0 ? <p> 爪注转 转转.</p> : (
              <ul>
                 {data.proposals.map((p, i) => <li key={i}>爪注 {i + 1}</li>)}
              </ul>
            )}
          </div>
        )}
      </div>
    </div>
  );
}

export default Dashboard;
