import React, { useState, useEffect } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import api from '../api/axios';
import '../css/FullPage.css';

const ShootingPage = () => {
  const navigate = useNavigate();
  const location = useLocation();
  
  // מקבל את הנתונים שהועברו מהדשבורד
  const { drills, isReadOnly, playerId } = location.state || {};
  const [text, setText] = useState(drills || '');

  // אם מישהו מנסה להיכנס ישירות לקישור בלי לעבור בדשבורד
  useEffect(() => {
    if (!location.state) {
      navigate('/player-zone');
    }
  }, [location.state, navigate]);

  const handleSave = async () => {
    try {
      const summary = text.split('\n')[0].substring(0, 30);
      
      // עדכון בשרת (אם זה שחקן עורך את עצמו או מאמן)
      // הערה: כאן אני מניח לוגיקה פשוטה, אפשר להרחיב לפי הצורך
      // כרגע נחזיר את המשתמש אחורה והוא יראה את השינוי אם נשמור ב-LocalStorage בדשבורד
      // או שנבצע קריאת API כאן. לשם פשטות נחזור אחורה עם המידע.
      
      alert('השינויים נשמרו מקומית (יש ללחוץ שמירה בדשבורד כדי לעדכן סופית)');
      navigate(-1, { state: { updatedDrills: text, updatedSummary: summary } });
      
    } catch (err) {
      console.error(err);
      alert('שגיאה בשמירה');
    }
  };

  return (
    <div className="full-page-container">
      <div className="page-header header-shooting">
        <h1 className="page-title">🏀 אימון קליעה - מסך מלא</h1>
        <button className="btn-back" onClick={() => navigate(-1)}>חזור לדשבורד ↩</button>
      </div>

      <div className="content-box">
        {isReadOnly ? (
          <div className="text-content">{text}</div>
        ) : (
          <>
            <textarea 
              className="edit-textarea" 
              value={text} 
              onChange={(e) => setText(e.target.value)}
            />
            <div className="action-buttons">
              <button className="btn-save" onClick={handleSave}>שמור וסיים</button>
            </div>
          </>
        )}
      </div>
    </div>
  );
};

export default ShootingPage;
