import React, { useState, useEffect } from 'react';
import '../css/Dashboard.css'; //  砖 转  拽抓 -CSS

const Dashboard = () => {
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [data, setData] = useState(null);
  const [user, setUser] = useState(null);
  const [view, setView] = useState('roster');

  // 驻拽爪转 爪 转 - 拽转  注专 
  const handleManualLogout = () => {
    localStorage.clear();
    sessionStorage.clear();
    window.location.href = '/login';
  };

  useEffect(() => {
    let isMounted = true; 

    const fetchData = async () => {
      try {
        const token = localStorage.getItem('token');

        // 1. 拽  砖 拽 
        if (!token) {
          throw new Error(" 爪 砖专 住 (Token).  转专 砖.");
        }

        const response = await fetch('http://localhost:5000/api/dashboard', {
          method: 'GET',
          headers: {
            'Content-Type': 'application/json',
            'Authorization': `Bearer ${token}` 
          }
        });

        // 2. 驻 砖转 砖专转 (401/403) -  专注 !
        if (!response.ok) {
          if (response.status === 401 || response.status === 403) {
            throw new Error("驻 转拽祝 专 (砖 403). 砖 转专 砖.");
          }
          const errorText = await response.text();
          throw new Error(errorText || "砖 转 拽转 转");
        }

        // 3. 专 -JSON 专拽   转拽
        const result = await response.json();

        if (isMounted) {
          setData({
            players: result.players || result.roster || [],
            proposals: result.proposals || []
          });
          setUser(result.user || { name: '砖转砖', role: 'unknown' });
          setLoading(false);
        }

      } catch (err) {
        console.error("Dashboard Error:", err);
        if (isMounted) {
          setError(err.message);
          setLoading(false); // 注爪专 转 注  爪 转 砖
        }
      }
    };

    fetchData();

    return () => { isMounted = false; };
  }, []);

  // --- 转爪转 注 (注 驻转专 专) ---
  if (loading) {
    return (
      <div className="dashboard-container" style={{textAlign: 'center', marginTop: '50px'}}>
        <h2>注 转...</h2>
        <button onClick={handleManualLogout} className="btn-logout" style={{marginTop: '20px', backgroundColor: 'gray'}}>
            转拽注 注? 抓  爪
        </button>
      </div>
    );
  }

  // --- 转爪转 砖 (爪,  专爪转) ---
  if (error) {
    return (
      <div className="dashboard-container" style={{textAlign: 'center', marginTop: '50px'}}>
        <div className="error-box" style={{padding: '20px', border: '2px solid red', borderRadius: '10px', display: 'inline-block'}}>
            <h2 style={{color: 'red'}}>砖 注转 祝</h2>
            <p style={{fontSize: '18px'}}>{error}</p>
            <br />
            <button onClick={handleManualLogout} className="btn-logout">
                专 祝 转专转 (Login)
            </button>
        </div>
      </div>
    );
  }

  // --- 转爪 专砖转 (专拽 砖 转拽) ---
  return (
    <div className="dashboard-container">
      <div className="dashboard-header">
        <div>
             <h1>Mars Power Drive </h1>
             <span>砖, <strong>{user?.name}</strong> ({user?.role === 'coach' ? '' : '砖拽'})</span>
        </div>
        <button onClick={handleManualLogout} className="btn-logout">转转拽</button>
      </div>

      <div className="content-box">
        {/* 驻转专  */}
        <div className="nav-tabs">
            <button className={`nav-btn ${view === 'roster' ? 'active' : ''}`} onClick={() => setView('roster')}>
                 住 砖拽
            </button>
            {user?.role === 'coach' && (
                <button className={`nav-btn ${view === 'approvals' ? 'active' : ''}`} onClick={() => setView('approvals')}>
                    锔 砖专 ({data.proposals.length})
                </button>
            )}
        </div>

        {/* 转: 住 */}
        {view === 'roster' && (
            <div className="players-grid">
                {data.players.length === 0 ? <p> 砖拽 爪</p> : data.players.map((player, index) => (
                    <div key={index} className="card-item">
                        <strong>{player.name || player.userId?.fullName || '砖拽'}</strong>
                        <div>注: {player.position || '-'} | : {player.height || '?'}</div>
                    </div>
                ))}
            </div>
        )}

        {/* 转: 砖专 () */}
        {view === 'approvals' && (
            <div className="proposals-list">
                {data.proposals.length === 0 ? <p> 拽砖转 转转</p> : data.proposals.map((prop, index) => (
                    <div key={index} className="card-item" style={{borderColor: 'orange'}}>
                        <strong>拽砖 砖:</strong> {prop.playerId?.name || '砖拽'}
                        <br/>
                        <button className="btn-primary" style={{marginTop: '10px'}}>砖专 拽砖</button>
                    </div>
                ))}
            </div>
        )}
      </div>
    </div>
  );
};

export default Dashboard;
