import React, { useEffect, useState } from 'react';
import api from '../api/axios';

// 拽 转 initialData -prop ( 注 砖专 注 -Dashboard 专砖)
const CoachDashboard = ({ initialData }) => {
  //  砖 注 专砖 - 砖转砖 .   - 注专 专拽.
  const [players, setPlayers] = useState(initialData?.players || []);
  const [editingId, setEditingId] = useState(null);
  const [form, setForm] = useState({ shooting: '', fitness: '', nutrition: '' });
  const [msg, setMsg] = useState('');

  // 驻拽爪 专注 转 (砖转砖  专拽 专 注)
  const loadPlayers = async () => {
    try {
      const res = await api.get('/team/players');
      setPlayers(res.data);
    } catch (err) { console.error("Error loading players:", err); }
  };

  const startEdit = (p) => {
    setEditingId(p._id);
    setForm({
      shooting: p.trainingPlan?.shooting || '',
      fitness: p.trainingPlan?.fitness || '',
      nutrition: p.nutritionPlan || ''
    });
  };

  const submitProposal = async (id) => {
    try {
      const payload = {
        trainingPlan: { shooting: form.shooting, fitness: form.fitness },
        nutritionPlan: form.nutrition
      };
      const res = await api.post(`/team/propose-update/${id}`, payload);
      setMsg(res.data.msg);
      setEditingId(null);
      
      // 专拽 专 注 爪, 拽砖 砖专转 转 专砖 注转
      loadPlayers();
      
      setTimeout(() => setMsg(''), 5000);
    } catch (err) {
      alert(err.response?.data?.msg || '砖 砖专');
    }
  };

  return (
    <div className="dashboard-container">
      <header className="dash-header">
        <h1>驻拽  - Mars Power Drive</h1>
        <button onClick={() => {localStorage.clear(); window.location.href='/login'}}>爪</button>
      </header>
      
      {msg && <div className="notification">{msg}</div>}

      {/* 住驻转 拽:   砖拽, 爪 注 */}
      {players.length === 0 ? (
          <div style={{textAlign: 'center', marginTop: '50px', color: 'white'}}>
              <h3> 爪 砖拽 注专转...</h3>
              <p> 砖专爪转 转 node seed.js 砖专转</p>
          </div>
      ) : (
          <div className="roster-grid">
            {players.map(p => (
              <div key={p._id} className="player-card">
                <div className="card-header">
                  <h3>{p.name}</h3>
                  <span className="badge">#{p.jerseyNumber}</span>
                </div>
                
                {editingId === p._id ? (
                  <div className="edit-area">
                    <label> 拽注:</label>
                    <input value={form.shooting} onChange={e=>setForm({...form, shooting: e.target.value})} />
                    <label>砖专:</label>
                    <input value={form.fitness} onChange={e=>setForm({...form, fitness: e.target.value})} />
                    <label>转:</label>
                    <input value={form.nutrition} onChange={e=>setForm({...form, nutrition: e.target.value})} />
                    <div className="btn-group">
                      <button className="btn-save" onClick={() => submitProposal(p._id)}>砖 爪注 / 砖专</button>
                      <button className="btn-cancel" onClick={() => setEditingId(null)}></button>
                    </div>
                  </div>
                ) : (
                  <div className="info-area">
                    <p> <strong>拽注:</strong> {p.trainingPlan?.shooting}</p>
                    <p> <strong>砖专:</strong> {p.trainingPlan?.fitness}</p>
                    <p>ウ <strong>转:</strong> {p.nutritionPlan}</p>
                    
                    {p.pendingUpdate?.active && (
                       <div className="pending-box">
                         锔 转 砖专 ({p.pendingUpdate.approvals.length}/3)
                       </div>
                    )}
                    
                    <button className="btn-edit" onClick={() => startEdit(p)}> 转转</button>
                  </div>
                )}
              </div>
            ))}
          </div>
      )}
    </div>
  );
};

export default CoachDashboard;
