import React, { useState, useEffect } from 'react';
import '../css/Dashboard.css'; //  砖转 转

const Dashboard = () => {
  const [view, setView] = useState('roster'); // 专专转 : 住 砖拽
  const [data, setData] = useState({ players: [], proposals: [] });
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // --- 驻拽爪转 转转拽转 拽 (Hard Logout) ---
  const handleLogout = () => {
    console.log("Logging out...");
    localStorage.clear(); // 拽转 拽
    sessionStorage.clear();
    window.location.href = '/login'; // 专注  注专 祝 
  };

  useEffect(() => {
    const fetchData = async () => {
      try {
        const token = localStorage.getItem('token');
        
        // 1.   拽 - 专拽 爪 
        if (!token) {
          handleLogout();
          return;
        }

        // 2. 砖转 拽砖 注 Bearer Correctly
        const response = await fetch('http://localhost:5000/api/dashboard', {
          method: 'GET',
          headers: {
            'Content-Type': 'application/json',
            'Authorization': `Bearer ${token}` // 拽专:  Bearer 转 转 驻
          }
        });

        // 3. 驻 爪 砖 (注 转 -Unexpected token 'I')
        if (!response.ok) {
          //  砖专转 专拽 转 (401/403)
          if (response.status === 401 || response.status === 403) {
            handleLogout();
            return;
          }
          
          // 住 拽专 砖转 拽住 (拽 拽专住 注 JSON)
          const errorText = await response.text();
          throw new Error(errorText || `砖转 砖专转: ${response.status}`);
        }

        // 4. 专 -JSON ( 砖砖 注砖)
        const result = await response.json();
        
        // 专 转 ( 砖 拽专住  砖专转 专  砖)
        // 转    砖拽
        const playersList = result.players || result.roster || (Array.isArray(result) ? result : []);
        const proposalsList = result.proposals || [];
        
        setData({ players: playersList, proposals: proposalsList });
        setUser(result.user || { name: '砖转砖', role: result.role || 'guest' });

      } catch (err) {
        console.error("Dashboard Error:", err);
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  // --- 转爪转 注 ---
  if (loading) return <div className="dashboard-container"><div className="loading-spinner">注 转...</div></div>;

  // --- 转爪转 砖 ( 驻转专 爪) ---
  if (error) {
    return (
      <div className="dashboard-container">
        <div className="error-msg">
            <h2>砖 注转 转</h2>
            <p>{error}</p>
            <button onClick={handleLogout} className="btn btn-logout">住 转专 砖</button>
        </div>
      </div>
    );
  }

  // --- 转爪 专砖转 ---
  return (
    <div className="dashboard-container">
      
      {/* 转专转 注 */}
      <div className="dashboard-header">
        <div style={{display:'flex', alignItems:'center', gap:'10px'}}>
             <span style={{fontSize:'30px'}}></span>
             <h1>Mars Power Drive</h1>
        </div>
        
        <div className="user-info">
            <span>砖, <strong>{user?.name || '专'}</strong> ({user?.role === 'coach' ? '' : '砖拽'})</span>
            <button onClick={handleLogout} className="btn btn-logout">转转拽</button>
        </div>
      </div>

      {/* 驻转专  */}
      <div className="nav-tabs">
        <button 
            className={`nav-btn ${view === 'roster' ? 'active' : ''}`} 
            onClick={() => setView('roster')}
        >
             住 砖拽
        </button>
        
        {user?.role === 'coach' && (
            <button 
                className={`nav-btn ${view === 'approvals' ? 'active' : ''}`} 
                onClick={() => setView('approvals')}
            >
                锔 砖专 ({data.proposals.length})
            </button>
        )}
      </div>

      {/* 转 转祝 */}
      <div className="content-box">
        
        {/* 转爪转 住 */}
        {view === 'roster' && (
            <div>
                <h2 style={{marginTop: 0}}>专砖转 砖拽</h2>
                {data.players.length === 0 ? <p> 砖拽 爪.</p> : (
                    <div>
                        {data.players.map((player, index) => (
                            <div key={player._id || index} className="card-item">
                                <strong>{player.name || player.userId?.fullName || '砖拽  砖'}</strong>
                                <div style={{color:'#666', fontSize:'14px'}}>
                                    注: {player.position || ' 专'} | : {player.height || '?'} '
                                </div>
                            </div>
                        ))}
                    </div>
                )}
            </div>
        )}

        {/* 转爪转 砖专 () */}
        {view === 'approvals' && (
            <div>
                <h2 style={{marginTop: 0}}>拽砖转 砖专 砖</h2>
                {data.proposals.length === 0 ? <p> 拽砖转 转转 砖专.</p> : (
                    <ul>
                        {data.proposals.map((prop, i) => (
                            <li key={i} className="card-item" style={{borderRightColor: '#ffc107'}}>
                                拽砖 砖 转转  注专 {prop.playerId?.name || '砖拽'}
                            </li>
                        ))}
                    </ul>
                )}
            </div>
        )}

      </div>
    </div>
  );
};

export default Dashboard;
