import React, { useState, useEffect } from 'react';
import api from '../api/axios';

const CoachDashboard = ({ initialData }) => {
  const [players, setPlayers] = useState(initialData?.players || []);
  const [editingId, setEditingId] = useState("NONE"); // התחלה עם ערך ברור
  const [form, setForm] = useState({ shooting: '', fitness: '', nutrition: '' });
  const [msg, setMsg] = useState('');

  // טעינת נתונים אם חסרים
  useEffect(() => {
    if (!players || players.length === 0) {
      loadPlayers();
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  const loadPlayers = async () => {
    try {
      const res = await api.get('/players');
      setPlayers(res.data);
    } catch (err) {
      console.error("Error loading players:", err);
    }
  };

  const startEdit = (p) => {
    // בדיקה ויזואלית שהלחיצה עובדת
    const idToEdit = String(p._id);
    alert(`מנסה לערוך את שחקן: ${p.name}\nID: ${idToEdit}`); 
    setEditingId(idToEdit);
    
    setForm({
      shooting: p.trainingPlan?.shooting || '',
      fitness: p.trainingPlan?.fitness || '',
      nutrition: p.nutritionPlan || ''
    });
  };

  const submitProposal = async (id) => {
    try {
      const payload = {
        trainingPlan: { shooting: form.shooting, fitness: form.fitness },
        nutritionPlan: form.nutrition
      };
      const res = await api.post(`/propose-update/${id}`, payload);
      
      setMsg(res.data.msg);
      setEditingId("NONE");
      loadPlayers(); 
      setTimeout(() => setMsg(''), 5000);
    } catch (err) {
      alert(err.response?.data?.msg || 'שגיאה בשמירה');
    }
  };

  return (
    <div className="dashboard-container">
      <header className="dash-header">
        <h1>פיקוד מאמנים - דיבאג מוד 🛠️</h1>
        <button onClick={() => {localStorage.clear(); window.location.href='/login'}}>יציאה</button>
      </header>
      
      {/* --- אזור דיבאג שמופיע בראש המסך --- */}
      <div style={{background: 'black', color: '#0f0', padding: '10px', direction: 'ltr', textAlign: 'left'}}>
          <strong>CURRENT STATE:</strong><br/>
          Editing ID: {editingId}<br/>
          Players Loaded: {players.length}
      </div>

      {msg && <div className="notification">{msg}</div>}

      <div className="roster-grid">
        {players.length > 0 ? (
          players.map(p => (
            <div key={p._id} className="player-card">
              <div className="card-header">
                <h3>{p.name}</h3>
                {/* הצגת ה-ID על הכרטיס לבדיקה */}
                <span style={{fontSize: '10px', color: 'gray'}}>{p._id}</span>
              </div>
              
              {/* השוואה ויזואלית */}
              {String(editingId) === String(p._id) ? (
                <div className="edit-area" style={{background: '#e3f2fd', padding: '10px'}}>
                  <h4 style={{margin: '0 0 10px 0', color: 'blue'}}>מצב עריכה פעיל!</h4>
                  <label>אימון קליעה:</label>
                  <input value={form.shooting} onChange={e=>setForm({...form, shooting: e.target.value})} />
                  <label>כושר:</label>
                  <input value={form.fitness} onChange={e=>setForm({...form, fitness: e.target.value})} />
                  <label>תזונה:</label>
                  <input value={form.nutrition} onChange={e=>setForm({...form, nutrition: e.target.value})} />
                  <div className="btn-group">
                    <button className="btn-save" onClick={() => submitProposal(p._id)}>שמור</button>
                    <button className="btn-cancel" onClick={() => setEditingId("NONE")}>ביטול</button>
                  </div>
                </div>
              ) : (
                <div className="info-area">
                  <p>🏀 <strong>קליעה:</strong> {p.trainingPlan?.shooting || '---'}</p>
                  <p>💪 <strong>כושר:</strong> {p.trainingPlan?.fitness || '---'}</p>
                  
                  <button 
                      className="btn-edit" 
                      onClick={() => startEdit(p)}
                      style={{border: '2px solid red'}} /* הדגשת הכפתור */
                  >
                      לחץ כאן לעריכה
                  </button>
                </div>
              )}
            </div>
          ))
        ) : (
          <div style={{textAlign: 'center', marginTop: '50px', color: 'white'}}>
             <h3>טוען שחקנים...</h3>
          </div>
        )}
      </div>
    </div>
  );
};

export default CoachDashboard;
