import React, { useEffect, useState } from 'react';
import '../css/PlayerModal.css'; 
import '../css/CoachDashboard.css'; 
import ShootingModal from './ShootingModal';
import FitnessModal from './FitnessModal';
import NutritionModal from './NutritionModal'; // ייבוא

const PlayerDashboard = () => {
  const [player, setPlayer] = useState(null);
  const [showDrills, setShowDrills] = useState(false);
  const [showFitness, setShowFitness] = useState(false);
  const [showNutrition, setShowNutrition] = useState(false); // סטייט חדש

  useEffect(() => {
    const storedUser = localStorage.getItem('user');
    if (storedUser) {
      setPlayer(JSON.parse(storedUser));
    } else {
      window.location.href = '/login';
    }
  }, []);

  const calculateAge = (dateString) => {
    if (!dateString || dateString === 'לא צוין') return '---';
    const parts = dateString.split('/');
    if (parts.length !== 3) return '---';
    const birthDate = new Date(parts[2], parts[1] - 1, parts[0]);
    const today = new Date();
    let age = today.getFullYear() - birthDate.getFullYear();
    const m = today.getMonth() - birthDate.getMonth();
    if (m < 0 || (m === 0 && today.getDate() < birthDate.getDate())) age--;
    return age;
  };

  if (!player) return <div style={{textAlign:'center', marginTop:'50px'}}>טוען נתונים...</div>;

  return (
    <div className="coach-container">
      <header className="coach-header">
         <div className="header-title"><h1>האזור האישי - Mars Power Drive 🏀</h1></div>
         <button className="btn-logout" onClick={() => {localStorage.clear(); window.location.href='/login'}}>יציאה</button>
      </header>

      <div style={{display:'flex', justifyContent:'center', marginTop:'20px'}}>
        <div className="player-modal-content" style={{width:'100%', maxWidth:'1000px', boxShadow:'none', border:'1px solid #ddd', position: 'static', transform: 'none'}}>
            <div className="modal-body">
                {/* צד ימין */}
                <div className="player-details-side">
                    <img src={player.imageUrl} alt={player.name} className="player-large-img" onError={(e) => {e.target.src = 'https://via.placeholder.com/150'}} />
                    <h2 className="player-name-red">{player.name}</h2>
                    <div className="details-list">
                        <div className="detail-row"><strong>📍 עמדה:</strong> <span>{player.position}</span></div>
                        <div className="detail-row"><strong>👕 מספר גופייה:</strong> <span>#{player.jerseyNumber}</span></div>
                        <div className="detail-row"><strong>📏 גובה:</strong> <span>{player.height} מ'</span></div>
                        <div className="detail-row"><strong>📅 תאריך לידה:</strong> <span>{player.birthDate}</span></div>
                        <div className="detail-row"><strong>🎂 גיל:</strong> <span>{calculateAge(player.birthDate)}</span></div>
                    </div>
                </div>

                {/* צד שמאל */}
                <div className="form-side">
                    <h2 className="form-title">📋 התוכנית המקצועית שלי</h2>
                    
                    {/* אימון קליעה */}
                    <div className="form-group">
                        <label className="form-label">🏀 אימון קליעה (לחץ לפירוט):</label>
                        <div 
                            onClick={() => setShowDrills(true)}
                            className="clickable-input orange-border"
                        >
                             <span>{player.trainingPlan?.shooting || 'אין נתונים'}</span>
                             <span style={{fontSize: '0.8rem'}}>לחץ לפירוט 👈</span>
                        </div>
                    </div>

                    {/* כושר גופני */}
                    <div className="form-group">
                        <label className="form-label">💪 כושר גופני (לחץ לצפייה):</label>
                        <div 
                             onClick={() => setShowFitness(true)} 
                             className="clickable-input green-border"
                        >
                            <span>{player.trainingPlan?.fitness || 'אין נתונים'}</span>
                            <span style={{fontSize: '0.8rem'}}>לחץ לצפייה 👁️</span>
                        </div>
                    </div>

                    {/* תזונה - מעודכן! */}
                    <div className="form-group">
                        <label className="form-label">🥦 תזונה (לחץ לתפריט):</label>
                        <div 
                            onClick={() => setShowNutrition(true)}
                            className="clickable-input blue-border"
                        >
                            <span>{player.nutritionPlan || 'תפריט מאוזן'}</span>
                            <span style={{fontSize: '0.8rem'}}>לחץ לצפייה 🥗</span>
                        </div>
                    </div>
                    
                    <div style={{marginTop: '30px', textAlign: 'center', color: '#7f8c8d', borderTop: '1px solid #eee', paddingTop: '10px'}}>
                        <em>* לשינוי תוכנית יש לפנות לצוות המקצועי</em>
                    </div>
                </div>
            </div>
        </div>
      </div>

      {/* מודלים */}
      <ShootingModal 
        isOpen={showDrills}
        onClose={() => setShowDrills(false)}
        drills={player.trainingPlan?.shootingDrills || 'לא הוזן פירוט'}
        isReadOnly={true} 
      />

      <FitnessModal 
        isOpen={showFitness}
        onClose={() => setShowFitness(false)}
        planText={player.trainingPlan?.fitnessDrills} 
      />

      {/* המודל החדש */}
      <NutritionModal 
        isOpen={showNutrition}
        onClose={() => setShowNutrition(false)}
        menuText={player.nutritionMenu} 
      />
    </div>
  );
};

export default PlayerDashboard;
