import React, { useState, useEffect } from 'react';
import api from '../api/axios';

const CoachDashboard = ({ initialData }) => {
  const [players, setPlayers] = useState(initialData?.players || []);
  // 砖: 砖转砖   注专 (转专   住 转)
  const [editingEmail, setEditingEmail] = useState(null);
  const [form, setForm] = useState({ shooting: '', fitness: '', nutrition: '' });
  const [msg, setMsg] = useState('');

  useEffect(() => {
    if (!players || players.length === 0) {
      loadPlayers();
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  const loadPlayers = async () => {
    try {
      const res = await api.get('/players');
      setPlayers(res.data);
    } catch (err) {
      console.error("Error loading players:", err);
    }
  };

  const startEdit = (p) => {
    //  驻  -  转 注
    setEditingEmail(p.email);
    
    setForm({
      shooting: p.trainingPlan?.shooting || '',
      fitness: p.trainingPlan?.fitness || '',
      nutrition: p.nutritionPlan || ''
    });
  };

  const submitProposal = async (id) => {
    try {
      const payload = {
        trainingPlan: { shooting: form.shooting, fitness: form.fitness },
        nutritionPlan: form.nutrition
      };
      const res = await api.post(`/propose-update/${id}`, payload);
      
      setMsg(res.data.msg);
      setEditingEmail(null); // 住专 转 注专
      loadPlayers(); // 专注 转
      setTimeout(() => setMsg(''), 5000);
    } catch (err) {
      alert(err.response?.data?.msg || '砖 砖专');
    }
  };

  return (
    <div className="dashboard-container">
      <header className="dash-header">
        <h1>驻拽  - Mars Power Drive</h1>
        <button onClick={() => {localStorage.clear(); window.location.href='/login'}}>爪</button>
      </header>
      
      {msg && <div className="notification">{msg}</div>}

      <div className="roster-grid">
        {players.length > 0 ? (
          players.map(p => (
            <div key={p._id} className="player-card">
              <div className="card-header">
                <h3>{p.name}</h3>
                <span className="badge">#{p.jerseyNumber}</span>
              </div>
              
              {/* 拽 驻  - 驻转专 转 注转 驻转专 */}
              {editingEmail === p.email ? (
                <div className="edit-area" style={{backgroundColor: '#f0f8ff', padding: '10px', borderRadius: '5px'}}>
                  <h4 style={{marginTop: 0, color: '#007bff'}}>锔 爪 注专</h4>
                  
                  <label>  拽注:</label>
                  <input 
                    value={form.shooting} 
                    onChange={e=>setForm({...form, shooting: e.target.value})} 
                    style={{width: '95%', padding: '5px', marginBottom: '5px'}}
                  />
                  
                  <label> 砖专:</label>
                  <input 
                    value={form.fitness} 
                    onChange={e=>setForm({...form, fitness: e.target.value})} 
                    style={{width: '95%', padding: '5px', marginBottom: '5px'}}
                  />
                  
                  <label>ウ 转:</label>
                  <input 
                    value={form.nutrition} 
                    onChange={e=>setForm({...form, nutrition: e.target.value})} 
                    style={{width: '95%', padding: '5px', marginBottom: '10px'}}
                  />
                  
                  <div className="btn-group">
                    <button className="btn-save" onClick={() => submitProposal(p._id)}>砖专 砖</button>
                    <button className="btn-cancel" onClick={() => setEditingEmail(null)}></button>
                  </div>
                </div>
              ) : (
                <div className="info-area">
                  <p> <strong>拽注:</strong> {p.trainingPlan?.shooting || '---'}</p>
                  <p> <strong>砖专:</strong> {p.trainingPlan?.fitness || '---'}</p>
                  {/* 专转 转 砖专转 转! */}
                  <p>ウ <strong>转:</strong> {p.nutritionPlan || '---'}</p>
                  
                  {p.pendingUpdate?.active && (
                     <div className="pending-box" style={{color: 'orange', marginTop: '10px', fontWeight: 'bold'}}>
                       锔 转 砖专 ({p.pendingUpdate.approvals.length}/3)
                     </div>
                  )}
                  
                  <button className="btn-edit" onClick={() => startEdit(p)}> 转转</button>
                </div>
              )}
            </div>
          ))
        ) : (
          <div style={{textAlign: 'center', marginTop: '50px', color: 'white'}}>
             <h3>注 砖拽...</h3>
          </div>
        )}
      </div>
    </div>
  );
};

export default CoachDashboard;
