import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import CoachDashboard from './CoachDashboard';
import PlayerDashboard from './PlayerDashboard';
import api from '../api/axios'; // שימוש ב-axios המוגדר שלנו
import '../css/Dashboard.css';

const Dashboard = () => {
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [userRole, setUserRole] = useState(null);
  const [userData, setUserData] = useState(null);
  const navigate = useNavigate();

  const handleLogout = () => {
    localStorage.clear();
    navigate('/login');
  };

  useEffect(() => {
    const fetchData = async () => {
      try {
        // שליפה מנתיב הדאשבורד החדש
        const res = await api.get('/dashboard');
        
        // השרת מחזיר מבנה שונה למאמן ולשחקן, נזהה לפי התפקיד בטוקן או בתשובה
        // נניח שהשרת מחזיר אובייקט עם user.role למאמנים, או ישירות את ה-User לשחקנים
        const role = res.data.user ? res.data.user.role : res.data.role;
        
        setUserRole(role);
        setUserData(res.data);
        setLoading(false);
      } catch (err) {
        console.error("Dashboard Error:", err);
        setError("נכשל בטעינת נתונים. נסה להתחבר מחדש.");
        setTimeout(handleLogout, 2000); // זורק החוצה אם נכשל
      }
    };

    fetchData();
  }, []);

  if (loading) return <div className="loader-container"><h3>טוען...</h3></div>;
  if (error) return <div className="error-text">{error}</div>;

  return (userRole === 'head-coach' || userRole === 'coach')
    ? <CoachDashboard initialData={userData} />
    : <PlayerDashboard initialData={userData} />;
};

export default Dashboard;
