import React, { useState, useEffect } from 'react';
import api from '../api/axios';

const CoachDashboard = ({ initialData }) => {
  const [players, setPlayers] = useState(initialData?.players || []);
  const [selectedPlayer, setSelectedPlayer] = useState(null);
  const [form, setForm] = useState({ shooting: '', fitness: '', nutrition: '' });
  const [msg, setMsg] = useState('');

  useEffect(() => {
    if (!players || players.length === 0) {
      loadPlayers();
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  const loadPlayers = async () => {
    try {
      const res = await api.get('/players');
      setPlayers(res.data);
    } catch (err) {
      console.error("Error loading players:", err);
    }
  };

  // --- פונקציה לחישוב גיל ---
  const calculateAge = (dateString) => {
    if (!dateString || dateString === 'לא צוין') return '---';
    
    // המרת הפורמט מ-DD/MM/YYYY לאובייקט תאריך
    const parts = dateString.split('/');
    if (parts.length !== 3) return '---';
    
    const birthDate = new Date(parts[2], parts[1] - 1, parts[0]);
    const today = new Date();
    
    let age = today.getFullYear() - birthDate.getFullYear();
    const m = today.getMonth() - birthDate.getMonth();
    
    // בדיקה אם יום ההולדת עבר השנה
    if (m < 0 || (m === 0 && today.getDate() < birthDate.getDate())) {
      age--;
    }
    
    return age;
  };

  const openPlayerPage = (p) => {
    setSelectedPlayer(p);
    setForm({
      shooting: p.trainingPlan?.shooting || '',
      fitness: p.trainingPlan?.fitness || '',
      nutrition: p.nutritionPlan || ''
    });
  };

  const closePlayerPage = () => {
    setSelectedPlayer(null);
    setMsg('');
  };

  const submitUpdate = async () => {
    if (!selectedPlayer) return;

    try {
      const payload = {
        trainingPlan: { shooting: form.shooting, fitness: form.fitness },
        nutritionPlan: form.nutrition
      };
      
      const res = await api.post(`/propose-update/${selectedPlayer._id}`, payload);
      setMsg("✅ " + res.data.msg);
      loadPlayers(); // רענון הנתונים ברקע
      
      // עדכון הנתונים המוצגים כרגע בחלונית (כדי שהשינוי ייראה מיד)
      setSelectedPlayer(prev => ({
          ...prev, 
          trainingPlan: payload.trainingPlan, 
          nutritionPlan: payload.nutritionPlan 
      }));

      setTimeout(() => setMsg(''), 3000);
      
    } catch (err) {
      alert(err.response?.data?.msg || 'שגיאה בשמירה');
    }
  };

  return (
    <div className="dashboard-container">
      <header className="dash-header">
        <h1>פיקוד מאמנים - Mars Power Drive 🏀</h1>
        <button onClick={() => {localStorage.clear(); window.location.href='/login'}}>יציאה</button>
      </header>

      {/* --- גריד השחקנים --- */}
      <div className="roster-grid">
        {players.map(p => (
          <div key={p._id} className="player-card" style={{textAlign: 'center'}}>
            <img 
                src={p.imageUrl} 
                alt={p.name} 
                style={{width: '80px', height: '80px', borderRadius: '50%', objectFit: 'cover', border: '3px solid #3498db', marginBottom: '10px'}}
                onError={(e) => {e.target.src = 'https://via.placeholder.com/80'}} 
            />
            <h3>{p.name}</h3>
            <span className="badge" style={{display: 'block', margin: '5px auto', width: 'fit-content'}}>
              #{p.jerseyNumber} | {p.position}
            </span>
            
            <button className="btn-edit" onClick={() => openPlayerPage(p)}>
              ניהול תוכנית / דף שחקן
            </button>
          </div>
        ))}
      </div>

      {/* --- דף שחקן (Modal) --- */}
      {selectedPlayer && (
        <div className="player-modal-overlay" style={overlayStyle}>
          <div className="player-modal-content" style={modalStyle}>
            <button onClick={closePlayerPage} style={closeBtnStyle}>✖ סגור</button>
            
            <div style={{display: 'flex', gap: '30px', alignItems: 'flex-start', flexWrap: 'wrap'}}>
                
                {/* צד ימין: תמונה ופרטים אישיים */}
                <div style={{flex: '1', minWidth: '250px', textAlign: 'center', background: '#f8f9fa', padding: '20px', borderRadius: '10px'}}>
                    <img 
                        src={selectedPlayer.imageUrl} 
                        alt={selectedPlayer.name}
                        style={{width: '180px', height: '180px', borderRadius: '15px', objectFit: 'cover', marginBottom: '15px', border: '4px solid #fff', boxShadow: '0 4px 8px rgba(0,0,0,0.1)'}} 
                        onError={(e) => {e.target.src = 'https://via.placeholder.com/150'}}
                    />
                    <h2 style={{margin: '10px 0', color: '#2c3e50'}}>{selectedPlayer.name}</h2>
                    
                    <div style={{textAlign: 'right', marginTop: '20px', fontSize: '16px', lineHeight: '2.2'}}>
                        <div style={rowStyle}><strong>📍 עמדה:</strong> <span>{selectedPlayer.position}</span></div>
                        <div style={rowStyle}><strong>👕 מספר גופייה:</strong> <span>#{selectedPlayer.jerseyNumber}</span></div>
                        <div style={rowStyle}><strong>📏 גובה:</strong> <span>{selectedPlayer.height} מ'</span></div>
                        <div style={rowStyle}><strong>📅 תאריך לידה:</strong> <span>{selectedPlayer.birthDate}</span></div>
                        <div style={rowStyle}><strong>🎂 גיל:</strong> <span>{calculateAge(selectedPlayer.birthDate)}</span></div>
                    </div>
                </div>

                {/* צד שמאל: ניהול תוכנית */}
                <div style={{flex: '1.5', minWidth: '300px'}}>
                    <h2 style={{marginTop: 0, borderBottom: '3px solid #3498db', paddingBottom: '10px', color: '#3498db'}}>
                       📋 תוכנית עבודה מקצועית
                    </h2>
                    
                    {msg && <div style={{background: '#d4edda', color: '#155724', padding: '15px', borderRadius: '8px', marginBottom: '20px', fontWeight: 'bold'}}>{msg}</div>}

                    <div className="form-group">
                        <label style={{fontWeight: 'bold', display: 'block', marginBottom: '5px'}}>🏀 אימון קליעה:</label>
                        <input 
                            value={form.shooting} 
                            onChange={e=>setForm({...form, shooting: e.target.value})} 
                            style={inputStyle}
                        />
                    </div>

                    <div className="form-group" style={{marginTop: '15px'}}>
                        <label style={{fontWeight: 'bold', display: 'block', marginBottom: '5px'}}>💪 כושר גופני:</label>
                        <input 
                            value={form.fitness} 
                            onChange={e=>setForm({...form, fitness: e.target.value})} 
                            style={inputStyle}
                        />
                    </div>

                    <div className="form-group" style={{marginTop: '15px'}}>
                        <label style={{fontWeight: 'bold', display: 'block', marginBottom: '5px'}}>🥦 תזונה:</label>
                        <input 
                            value={form.nutrition} 
                            onChange={e=>setForm({...form, nutrition: e.target.value})} 
                            style={inputStyle}
                        />
                    </div>

                    <div style={{marginTop: '30px', display: 'flex', gap: '15px'}}>
                        <button onClick={submitUpdate} className="btn-save" style={{padding: '12px 25px', fontSize: '16px', flex: 1}}>💾 שמור עדכונים</button>
                        <button onClick={closePlayerPage} className="btn-cancel" style={{padding: '12px 25px', fontSize: '16px', flex: 0.5}}>ביטול</button>
                    </div>
                </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

// --- עיצובים (Styles) ---
const overlayStyle = {
  position: 'fixed', top: 0, left: 0, right: 0, bottom: 0,
  backgroundColor: 'rgba(0,0,0,0.85)', display: 'flex', justifyContent: 'center', alignItems: 'center', zIndex: 1000,
  backdropFilter: 'blur(5px)'
};

const modalStyle = {
  backgroundColor: 'white', padding: '40px', borderRadius: '20px', width: '90%', maxWidth: '1000px',
  boxShadow: '0 20px 50px rgba(0,0,0,0.5)', position: 'relative', direction: 'rtl', maxHeight: '90vh', overflowY: 'auto'
};

const closeBtnStyle = {
  position: 'absolute', top: '20px', left: '25px', background: 'none', border: 'none', 
  fontSize: '24px', cursor: 'pointer', color: '#7f8c8d', transition: 'color 0.3s'
};

const inputStyle = {
  width: '100%', padding: '12px', fontSize: '16px', border: '2px solid #ecf0f1', borderRadius: '8px', 
  marginTop: '5px', outline: 'none', transition: 'border-color 0.3s'
};

const rowStyle = {
    display: 'flex', justifyContent: 'space-between', borderBottom: '1px solid #ecf0f1', padding: '8px 0'
};

export default CoachDashboard;
