import React, { useState } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import api from '../api/axios'; 
import '../css/FullPage.css';

const ShootingPage = () => {
  const navigate = useNavigate();
  const location = useLocation();
  
  // לוקחים מהניווט רק את הטקסטים להצגה ראשונית ואת ה-ID
  const { text: initialText, title: initialTitle, isReadOnly, playerId, returnPath } = location.state || {};
  
  const [text, setText] = useState(initialText || '');
  const [title, setTitle] = useState(initialTitle || '');

  const handleBack = () => {
      if (returnPath && playerId) {
          // חוזרים ומבקשים רענון
          navigate(returnPath, { state: { restorePlayerId: playerId } });
      } else {
          navigate(-1);
      }
  };

  const handleSave = async () => {
    if (!playerId) {
        alert("שגיאה: חסר מזהה שחקן");
        return;
    }

    try {
      // 1. שלב קריטי: מושכים את השחקן העדכני ביותר מהשרת לפני השמירה
      // זה מונע מצב שאנחנו דורסים את הכושר הגופני עם מידע ישן
      const response = await api.get('/players'); 
      const currentPlayer = response.data.find(p => p._id === playerId);

      if (!currentPlayer) {
          alert("שגיאה: השחקן לא נמצא בשרת");
          return;
      }

      // 2. לוקחים את התוכנית המלאה והמעודכנת מהשרת
      const currentPlan = currentPlayer.trainingPlan || {};

      // 3. מעדכנים בתוכה רק את הקליעה (הכושר והשאר נשמרים!)
      const updatedTrainingPlan = {
          ...currentPlan,        // שומר על כושר, משקל, וכל מה שהיה שם
          shootingDrills: text,  // מעדכן את הטקסט הארוך החדש
          shooting: title        // מעדכן את הכותרת החדשה
      };

      // 4. שולחים את המידע המשולב לשרת
      await api.post(`/propose-update/${playerId}`, {
        trainingPlan: updatedTrainingPlan
      });
      
      alert('✅ נשמר בהצלחה! (כל הנתונים בטוחים)');
      handleBack(); 
    } catch (err) {
      console.error(err);
      alert('❌ שגיאה בשמירה');
    }
  };

  return (
    <div className="full-page-container">
      <div className="page-header header-shooting">
        <h1 className="page-title">🏀 עריכת אימון קליעה</h1>
        <button className="btn-back" onClick={handleBack}>חזור ↩</button>
      </div>

      <div className="content-box">
        {isReadOnly ? (
          <>
            <h2 style={{borderBottom:'2px solid #eee', paddingBottom:'10px'}}>{title}</h2>
            <div className="text-display">{text}</div>
          </>
        ) : (
          <>
            <div style={{marginBottom: '20px'}}>
                <label style={{fontWeight:'bold', display:'block', marginBottom:'5px', color:'#333'}}>כותרת האימון (מה שרואים בדף השחקן):</label>
                <input 
                    type="text"
                    value={title}
                    onChange={(e) => setTitle(e.target.value)}
                    style={{width: '100%', padding: '10px', fontSize: '1.2rem', borderRadius: '5px', border: '1px solid #ccc', fontFamily: 'inherit', boxSizing:'border-box'}}
                    placeholder="לדוגמה: 310 זריקות"
                />
            </div>

            <label style={{fontWeight:'bold', display:'block', marginBottom:'5px', color:'#333'}}>פירוט התרגילים:</label>
            <textarea 
              className="edit-textarea" 
              value={text} 
              onChange={(e) => setText(e.target.value)}
              placeholder="כתוב כאן את תוכנית האימון..."
            />
            <div className="action-buttons">
              <button className="btn-save" onClick={handleSave}>💾 שמור שינויים בטוח</button>
            </div>
          </>
        )}
      </div>
    </div>
  );
};

export default ShootingPage;
