import axios from 'axios';

// יצירת מופע של Axios עם הגדרות בסיס
const api = axios.create({
  // שימוש ב-127.0.0.1 במקום localhost פותר בעיות חיבור נפוצות ב-Node v17+
  baseURL: 'http://127.0.0.1:5000/api',
  headers: {
    'Content-Type': 'application/json'
  }
});

// Interceptor: "תופס" כל בקשה לפני שהיא יוצאת
// ומוסיף לה את הטוקן (אם המשתמש מחובר)
api.interceptors.request.use(
  (config) => {
    const token = localStorage.getItem('token');
    if (token) {
      // הוספת הטוקן לכותרת הבקשה (Header)
      config.headers['x-auth-token'] = token;
    }
    return config;
  },
  (error) => {
    return Promise.reject(error);
  }
);

// Interceptor: טיפול בשגיאות תגובה
api.interceptors.response.use(
  (response) => response,
  (error) => {
    if (error.response && error.response.status === 401) {
      // אם קיבלנו שגיאת "לא מורשה" (401), ייתכן שהטוקן פג תוקף
      // אפשר להוסיף כאן לוגיקה לזריקת המשתמש ללוגין
      // localStorage.clear();
      // window.location.href = '/login';
    }
    return Promise.reject(error);
  }
);

export default api;
